	function dynamicContent() {
		// ********** MYSTART ********** //

        //FIND TODAY'S DATE
        var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth()+1; //January is 0!
        var yyyy = today.getFullYear();
        if(dd<10){
            dd='0'+dd;
        } 
        if(mm<10){
            mm='0'+mm;
        } 
        var today = mm+'/'+dd+'/'+yyyy;

        //INSERT 'SCHOOLS TODAY' AND TODAY'S DATE HEADER WHEN ANNOUNCEMENT IS PRESENT
        $("
SCHOOLSTODAY"+today+"").insertBefore("#gb-mystart-left .ui-article-description");

        //IF THERE'S A MYSTART ANNOUNCEMENT APP ON HOMEPAGE, LOAD IT INTO THE SUBPAGE MYSTART
        if($(".sp").length) {
            CSAPI.FlexData.GetFlexData(4, function(response) {
                // SUCCESS
                if(response.successful) {
                	var articles = '';
                    $.each(response.data, function(index, article) {
                    	articles += '
' +
                                    	'
' +
                                        	'
' +
                                            	article.cd_Announcement +
                                            '' +
                                        '' +
                                    '';
                    });

					$(".sp #gb-mystart-left").html('' +
                    	'
' +
                        	'
' +
                            '
' +
                            	'' +
                                	articles +
                                '' +
                            '' +
                            '
' +
                        '' +
                    '');

                    $("
SCHOOLSTODAY"+today+"").insertBefore(".sp #gb-mystart-left .ui-article-description");
                }

                // ERROR
                else {
                    console.log(response.data);
                }
            });
        }

        // COMMENTED OUT BY BRENTON KELLY DUE TO PERFORMANCE REASONS - 3.24.20
        /*$(".sp #gb-mystart-left" ).load( "/ #sw-content-container1", function() {
