        .on('keypress', function(e) { // hooking into the default button trigger so we can stop it and run it only on keyup since we auto focus the search bar
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                e.preventDefault();
            }
        })
        .on('keyup', function(e) { // Trigger the click event from the keyboard
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                $(this).click();
            }
