    function rsMenu(sortedSchools) {
        // DEFINE THE HEADER COUNTER
        // NEEDS TO BE DYNAMIC BECAUSE WE CAN'T BE SURE HOW MANY SCHOOL TYPES WE'LL HAVE
        var headerCounter = 0;

        // BUILD THE SCHOOL MENU STRUCTURE
        var schoolHeaders = "";
        var schoolItems = "";
        $.each(sortedSchools, function(type, schools) {
            var typeName = type.replace(/\s/g, "-").replace(/&/g, "and");
            type = type.replace(/charter/, "charter/contract");
            type = type.replace(/other/, "specialty"); //ADDED BY LYNDSAY - CASE 00537026 - 5.22.17

            // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
            schools.sort(function(a, b) {
                if(a.schoolName > b.schoolName) {
                    return 1;
                }
                if(a.schoolName < b.schoolName) {
                    return -1;
                }
                return 0;
            });

            headerCounter++; // WE HAVE A HEADER SO INCREMENT BY 1
            schoolHeaders += type + ";";

            for(var school in schools) {
                schoolItems += (headerCounter) + ',' + schools[school].schoolName + ',' + schools[school].schoolUrl + ',_self;';
            }
        });

        // BUILD THE GLOBAL ICONS
        var globalIcons = [
            ["Site Utility/8.png", "School Calendar", "/Page/2", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            ["Student Program/43.png", "School Meals", "http://www.aacps.org/nutrition", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            ["People/29.png", "Student/Parent Portal", "/Page/1325", "_self"],  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            ["/cms/lib011/MD02215556/Centricity/template/globalassets/images/icons/global-icon-paper.png", "Vendors and Contractors", "/Page/1391", "_self"],  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            ["Student Program/8.png", "School Security", "/Page/1039", "_self"],  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            ["/cms/lib/MD02215556/Centricity/Template/GlobalAssets/icons/57.png", "Safety Hotline", "/Page/8178", "_self"],  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            ["Classroom/22.png", "News Releases", "http://www.aacpsschools.org/pressr", "_blank"],  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
            ["Classroom/4.png", "School Year   2019-2020", "/Page/2504", "_self"]  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
        ];
        var iconNum = ("8" == "8;7;6;5;4;3;2;1;0") ? 0 : "8";
        var globalLinksHeader = "", globalLinks = "";

        if(iconNum > 0) {
            globalLinksHeader = "Global Links";
            headerCounter++; // WE HAVE A HEADER SO INCREMENT BY 1
        }

        for(var i = 0; i < iconNum; i++) {
            //var linkCheck = (globalIcons[i][2] == "[SITECALENDARLINK]") ? "/Page/2" : globalIcons[i][2];
            globalLinks += (headerCounter) + ',' + globalIcons[i][1] + ',' + globalIcons[i][2] + ',' + globalIcons[i][3] + ';';
        }

        // RESPONSIVE MAIN MENU
        $("body").csRsMenu({
            "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
            "slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left   
            "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
            "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
            "accentColor"           : "#BABABA", // DEFAULT - #BABABA
            "menuButtonParent"      : "#gb-mystart-right",
            "showMenuBtnText"       : "yes",
            "menuBtnText"           : "MENU",
            "showSchools"           : "no",
            "showTranslate"         : "no",
            "translateId" : "none",
            "advancedTranlate": {
                "useAdvancedList": "yes", // yes = USE THE NEXT TWO OPTIONS BELOW, no = USE THE REGULAR LIST OF 11 LANGUAGES
            },
            "translateId"           : "none",
            "showAccount"           : "yes",
            "extraMenuOptions"      : schoolHeaders + globalLinksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
            "extraMenuItems"        : schoolItems + globalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
            "siteID"                : "4",
            "allLoaded"             : function(){}
        });
    }

    function csAppAccordion() {
    	head.ready("csAppAccordion", function(){
            $(".hp.row.one, .hp.row.two").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });
        });
	}

    	function tabAccordion(){
		if(!tabAccordionSet) {
			// MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
			// ACCORDION HEADER CLICK
			$("#sw-content-container2.ui-hp div.ui-widget-header").click(function(){
				if($(this).parent().hasClass("active")) {
					$(this).parent().removeClass("active");
				} else {
					$("#sw-content-container2.ui-hp div.ui-widget.app.active").removeClass("active");
					$(this).parent().addClass("active");

					$("html, body").animate({
						scrollTop: $(this).offset().top
					}, 500, function(){
						$("html, body").stop(true, false);
					});
				}
			});

			tabAccordionSet = true;
		}
	}

	function tabAccordionReset() {
		if(tabAccordionSet) {
			$("#sw-content-container2.ui-hp div.ui-widget-header").unbind("click");

			tabAccordionSet = false;
		}
	}

