$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=JgtcPuz%2fnDHeMRrY3Shnww%3d%3d&SessionID=7e991db1-1232-490d-9259-db2ad0155296&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f26%2f2020+4%3a47%3a51+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.78.29';
