div.script:
    var _slideDelay = 1000 * 7.9; // delay between slides
    var _fadeSpeed = 1000 * 1.5; // time to fade from one slide to the next
    var _fader;

    $(document).ready(function () {
        ShowPhoto($("#thumbnails div:eq(0)"), false);
        $("#thumbnails div").click(function () {
            // if the eu clicks a slide then stop the intervaler
            clearInterval(_fader);
            // _fader = setInterval(FadeToNext, _slideDelay);
            ShowPhoto($(this));
        });

        $("#nextSlide").click(function () {
            if ($("#thumbnails div:visible").length <= 5) return;
            $("#thumbnails div:visible:eq(0)").hide();
            UpdateArrows();
        });

        $("#previousSlide").click(function () {
            $("#thumbnails div:visible:eq(0)").prev().show();
            UpdateArrows();
        });

        UpdateArrows();

        $(".resultsTabs div.tab").click(function () { ShowTournamentResult($(this).attr("id")); });
        $(".resultsTabs .tab:first").click(); // display first tabbed item (if any)

        _fader = setInterval(FadeToNext, _slideDelay);
    });

    function ShowTournamentResult(id) {
        $(".resultsTabs div.tab").removeClass("active");
        $(".resultsTabs div.tab#" + id).addClass("active");

        $(".results div.r").hide();
        $(".results div.r#result_" + id).show();
    }

    function FadeToNext() {
        var active = $("#thumbnails div.active");
        var next = active.next();
        if (next.length == 0) next = $("#thumbnails div:eq(0)");
        ShowPhoto(next, true);
    }

    function UpdateArrows() {
        // next arrow
        if ($("#thumbnails div:visible").length <= 5) $("#nextSlide").addClass("disabled");
        else $("#nextSlide").removeClass("disabled");

        // previous arrow
        if ($("#thumbnails div:visible:eq(0)").prev().length == 0) $("#previousSlide").addClass("disabled");
        else $("#previousSlide").removeClass("disabled");
    }

    function ShowPhoto(div, doFade) {
        $("#slides #fade").attr("src", $("#slides #main").attr("src"));
        $("#slides #fade").show();
        $("#slides").attr("style", "background:url('" + div.data("src") + "')");
        $("#slides #main").attr("alt", div.data("slidetitle"));
        $("#slides #fade").fadeOut(doFade ? _fadeSpeed : 0, "linear", function () { }); // anything that should happen after the fade should go in here

        $("#thumbnails div").removeClass("active");
        div.addClass("active");
        $("#slideshow a#clickAnchor").attr("href", div.data("url"));  
        $("#slideshow a#clickAnchor").attr("alt", div.data("slidetitle"));  
        $("#slideshow a#clickAnchor #clickArea").attr("title", div.data("slidetitle"));  
        $("#slides #slidetitle a").text(div.data("slidetitle")).attr("alt", "slide");
        $("#slides #slidesummary").html(div.data("slidesummary"));
        $("#slides #readmore a").attr("href", div.data("url"));
    }

