	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop":
				$("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").resize(function() {
					setSidebar();
				});
			break;
			case "768":
				cloneSidebarApps();
			break;
			case "640":
				cloneSidebarApps();
			break;
			case "480":
				cloneSidebarApps();
			break;
			case "320":
				cloneSidebarApps();
			break;
		}
	}

    function appAccordion() {
    	$("#hp-content, .sp.column.one").csAppAccordion({
            "accordionBreakpoints" : [768, 640, 480, 320]
        });
    }

