    function headlinesGallery() {
    	var headlinesData = {
            "Album": {
            	"transitiondelay": "8",
                "Images": []
            }
        }
        var headlineRecord = '';

        $("#sw-content-container10.ui-hp .ui-widget.app.headlines:first .ui-articles > li").each(function() {
        	headlineRecord = {
            	"photoname": $.trim($(".ui-article-title", this).text()),
                "src": (($(".ui-article-thumb img[src]", this).length) ? $(".ui-article-thumb img", this).attr("src").replace(/\s/g, "%20").replace("../../", "") : ""),
                "caption": $.trim($(".ui-article-description", this).text()),
                "link": $(".ui-article-title a", this).attr("href").replace("../..", ""),
                "target": (($(".ui-article-title a[target]", this).length) ? $(".ui-article-title a", this).attr("target") : "_self")
            }

            headlinesData.Album.Images.push(headlineRecord);
        });

        $("#sw-content-container10.ui-hp .ui-widget.app.headlines:first").creativeGalleryRotate({
            "galleryData": headlinesData,
            "efficientLoad" : "yes",
            "imageWidth" : 410,
            "imageHeight" : 275,
            "appHeader" : "no",
            "titles" : "yes",
            "captions" : "yes",
            "playPause" : "yes",
            "mainControls" : "yes",
            "galleryOverlay" : "no",
            "linkedElement" : ["image"], // ["image", "title", "overlay", "all"]
            "contMoreLink" : "no",
            "contMoreLinkText" : "Read More",
            "descMoreLink" : "yes",
            "descMoreLinkText" : "Read More >>",
            "bullets" : "no",
            "bulletControls" : "no",
            "thumbnails" : "no",
            "thumbnailWidth" : 122,
            "thumbnailHeight" : 52,
            "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
            "hoverPause" : "yes",
            "fullScreenRotator" : "no",
            "fullScreenBreakpoints" : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
            "autoRotate" : "yes",
            "transitionType" : "fade", // fade or slide
            "transitionSpeed" : 3,
            "customResize" : "no",
            "customResizeFunction" : function(element) {},
            "onImageLoad" : function() { },
            "allImagesLoaded" : function() { },
            "onThumbnailLoad" : function() { },
            "allThumbnailsLoaded" : function() { },
            "onTransitionStart" : function() { },
            "onTransitionEnd" : function() { },
            "allLoaded" : function() {
            	$("#hp-slideshow .rs-photo-gallery-container").prepend($("#hp-slideshow .rs-photo-gallery-control.play-pause"));
