				"imageHeight" : 295,
				'imageControls': "off",
				"useOverlay" : "off",
				'randomOrder':"off",
				"animateType" : "fade"
			});
		} else {
			$("#sw-content-container23.region.ui-hp").jflickrfeed({
				limit: 5,
				qstrings: {
					tags: "penn state, campus, buildings, aerial"
				},
				itemTemplate: ''
			});
		}

		// GLOBAL ICONS
		$("#gb-global-icons").creativeIcons({
		        "iconNum"       : "4", // NUMBER OF ICONS
		        "icons"         : [
		                ["Communication/18.png", "E-Mail", "https://accounts.google.com/ServiceLogin?service=mail", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
