	function pgScreenSize(){
		var getWidth = $(window).width();

		if (getWidth >= 1024) { //Desktop View
			multimediaRotator();
            if( $('#gb-sticky-page-header-outer #gb-mystart-buttons').length <= 0 ){
				$('#gb-mystart > .flex-cont').append($('#gb-mystart-buttons'));
			}
		}else{ //Mobile BPs
			if(getWidth >= 480){
				multimediaRotator();
			}

            if( getWidth < 640 ){
            	appView();
            }

            if( $('#sticky-gb-channel-list-outer #gb-mystart').length <= 0 ){
				$('#sticky-gb-channel-list > .max-width').append($('#gb-mystart-buttons'));
			}
		}

        searchText();
	}

