		                "onTransitionEnd"         : function() {
                        	captionClearChannelBar();
                        },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 
                        	if( $('#hp-slideshow .rs-gallery-desc').length > 0 ){
                            	$('#hp-slideshow .rs-gallery-desc').wrap('
');
                            	$('#hp-slideshow .rs-gallery-desc').children().wrapAll('
');
                            }

                            if( $('#hp-slideshow .rs-gallery-control.next').length > 0 ){
                            	$("#hp-slideshow .rs-gallery-container").append('
');
                                $('#hp-slideshow .rs-gallery-control-cont').append($('#hp-slideshow .rs-gallery-control.back, #hp-slideshow .rs-gallery-control.next'));
