body.script:
window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');
var header_height;

var mainMenu = document.getElementById('sitenav_main');
var shuffleMain = document.getElementById('shuffle_row');

function init() {
	topnavButton.onclick = toggleNav;
	header_height = $('#sitenav_main').offset().top;
	// this positions the fixed element when reloading the page whith the scroll over 0
	 if ($(window).scrollTop() > 0 ){
	 	mainMenu.classList.add('scrolled');
	 	shuffleMain.classList.add('scrolled');
	 }
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}

window.addEventListener('scroll', function(event) {
	if ( $(window).scrollTop() > header_height ) {
		mainMenu.classList.add('scrolled');
		shuffleMain.classList.add('scrolled');
		return;
	} else {
		mainMenu.classList.remove('scrolled');
		shuffleMain.classList.remove('scrolled');
		return;
	}
});
