             setCarouselHeight('#animated_announcement_35067');
            $('#animated_announcement_35067').carousel({
              interval: 1000 * 5            });
            function setCarouselHeight(id)
    {
                var slideHeight = [];
                $(id+' .item').each(function()
                {
                    // add all slide heights to an array
                    slideHeight.push($(this).height());
                });

                // find the tallest item
                max = Math.max.apply(null, slideHeight);

                // set the slide's height
                $(id+' .carousel-content').each(function()
                {
                    $(this).css('height',max+'px');
                });
            }
            $('.sc-pause').on('focus', function() {
                $('#animated_announcement_35067').carousel('pause');
            });
            $('.sc-pause').on('blur', function() {
                $('#animated_announcement_35067').carousel('cycle');
            });












