		                ["Communication/6.png", "MMS Portal", "https://ltsd.crportals.studentinformation.systems/", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
						["Student Program/15.png", "Food Service", "https://www.ltsd.org/domain/9", "_self"] // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        ],
