    function LoadRating2213975() {
    var ModuleInstanceID = 22;
    var FlexDataID = 13975;

        GetContent("https://www.pmsd.org//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating2213975", 2);
}

    function InsertRating2213975() {
    var ModuleInstanceID = 22;
    var FlexDataID = 13975;
    var Rating = $('#swmoduleinstancerating-hid-rating2213975').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating2213975();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.pmsd.org//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating2213975']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating2213975Values();
                InsertRating2213975($('#swmoduleinstancerating-hid-rating2213975').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating2213975Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating2213975']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating2213975').val((i + 1) / 2);
            }
        });
    }

