    function LoadRating2213997() {
    var ModuleInstanceID = 22;
    var FlexDataID = 13997;

        GetContent("https://www.pmsd.org//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating2213997", 2);
}

    function InsertRating2213997() {
    var ModuleInstanceID = 22;
    var FlexDataID = 13997;
    var Rating = $('#swmoduleinstancerating-hid-rating2213997').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating2213997();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.pmsd.org//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating2213997']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating2213997Values();
                InsertRating2213997($('#swmoduleinstancerating-hid-rating2213997').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating2213997Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating2213997']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating2213997').val((i + 1) / 2);
            }
        });
    }

