    function translateMover() {
        if (window.innerWidth < 991)
            $(".SearchLi").appendTo(".slicknav_nav");
        else
            $(".SearchLi").appendTo(".GoogleSearch");
    }
    $(document).ready(function () {
        translateMover();
    });
    $(window).resize(function () {
        translateMover();
    });







