		function showEventsInCalender(eventsList)
          {
            var pushdataToCalender = [];
            var eventsCount = eventsList.length;
            if(eventsCount > 0)
            {		
              var eventColor = '#1194cf';
              
              for(var i=0;i<eventsCount;i++)
              {
                var currentEvent = eventsList[i];
                var eventId = currentEvent['id'];
                var eventName = currentEvent['title'];
                var eventStart = currentEvent['start_date_time'];
                var eventEnd = currentEvent['end_date_time'];

                var eventURL = '/page/eventdetails/'+eventId;
                pushdataToCalender.push({'title':eventName,'start':eventStart,'end':eventEnd,'color':eventColor,'url': eventURL});
                   
              }
              console.log(pushdataToCalender);
            }
            
            
            $('#divEventCalender').fullCalendar({
              header: {
                left: 'prev,next today',
                center: 'title',
                right: 'month,agendaWeek,agendaDay,listWeek'
              },
              defaultView: 'month',
              timeFormat: 'hh:mm a', 
              eventLimit: true,
              events:pushdataToCalender,
              eventClick: function(currentevent) {
                console.log(currentevent);
                if (currentevent.url) {
                  window.open(currentevent.url, "_blank");
                  return false;
                }
              }
            });
            
            
          }
