            function setPreventHttpRedirectParam(url) {
                url = (url || '').trim();
                if (url === '') return url;
                var parser = document.createElement('a');
                parser.href = url;
                var search = '?preventHttpRedirect=1';
                if (parser.search.length > 0)
                    search += '&' + parser.search.substr(1);
                parser.search = search;
                return parser.href;
            }

