$(document).ready(function() {
	$('.main-slider').slick({
	  dots: true,
	  infinite: true,
	  adaptiveHeight: true,
	  autoplay: true,
	  autoplaySpeed: '5000',
	  appendDots: '.slideNav',
	  arrows:false
	});
	
	$('.eventsSlider').slick({
		infinite: true,
		speed: 500,
		slidesToShow: 4,
		slidesToScroll: 4,
		adaptiveHeight: true,
		arrows: true,
		prevArrow: 'Previous',
		nextArrow: 'Next',
		responsive: [
			{
			  breakpoint: 1300,
			  settings: {
				slidesToShow: 3,
				slidesToScroll: 3,
				infinite: true
			  }
			},
			{
			  breakpoint: 980,
			  settings: {
				slidesToShow: 2,
				slidesToScroll: 2
			  }
			},
			{
			  breakpoint: 480,
			  settings: {
				slidesToShow: 1,
				slidesToScroll: 1
			  }
			}
		  ]
	});
	
});
