	var LTIE9;
	function drawCurve() {
		if ($("#gb-channel-list-outer").length && $("#canvas-channel-bar").length){

			var canvas = document.getElementById("canvas-channel-bar");
			//IF IE
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
			var ctx = canvas.getContext("2d");
            if("mbs" == "cb" || "mbs" == "mbe") {
            	drawChannelCanvasSchools(ctx);
            } else if("mbs" == "ces") {
            	drawChannelCanvasCrestline(ctx);
            } else {
				drawChannelCanvas(ctx);
            }
		}
		if ($("#gb-channel-list-outer").length && $("#breakpoint-canvas").length){
			var j = document.getElementById("breakpoint-canvas");
			//IF IE
			if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(j);}
			var context = j.getContext("2d");
			drawCanvasB(context);
		}
	}

	function drawChannelCanvas(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(16.1, 90.3);
		ctx.bezierCurveTo(-0.4, 88.1, 0.1, 68.5, 0.1, 68.5);
		ctx.lineTo(16.6, 64.1);
		ctx.closePath();
		ctx.fillStyle = "rgb(109, 105, 75)";
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(1298.1, 90.3);
		ctx.bezierCurveTo(1314.6, 88.1, 1314.1, 68.5, 1314.1, 68.5);
		ctx.lineTo(1297.6, 64.1);
		ctx.closePath();
		ctx.fillStyle = "rgb(109, 105, 75)";
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(1315.4, 72.1);
		ctx.lineTo(1315.4, 4.0);
		ctx.bezierCurveTo(1315.4, 1.8, 1313.6, 0.0, 1311.4, 0.0);
		ctx.lineTo(4.0, 0.0);
		ctx.bezierCurveTo(1.8, 0.0, 0.0, 1.8, 0.0, 4.0);
		ctx.lineTo(0.0, 72.1);
		ctx.bezierCurveTo(0.0, 74.3, 20.4, 55.0, 660.4, 53.1);
		ctx.bezierCurveTo(1300.4, 51.1, 1315.4, 74.3, 1315.4, 72.1);
		ctx.closePath();
		ctx.fillStyle = '#004500';
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(20.4, 7.0);
		ctx.lineTo(1292.4, 7.0);
		ctx.bezierCurveTo(1294.6, 7.0, 1296.4, 8.8, 1296.4, 11.0);
		ctx.lineTo(1296.4, 52.4);
		ctx.bezierCurveTo(1296.4, 54.6, 1294.6, 56.4, 1292.4, 56.4);
		ctx.bezierCurveTo(1292.4, 56.4, 974.2, 46.4, 656.1, 46.4);
		ctx.bezierCurveTo(338.2, 46.4, 20.4, 56.4, 20.4, 56.4);
		ctx.bezierCurveTo(18.2, 56.4, 16.4, 54.6, 16.4, 52.4);
		ctx.lineTo(16.4, 11.0);
		ctx.bezierCurveTo(16.4, 8.8, 18.2, 7.0, 20.4, 7.0);
		ctx.closePath();
		ctx.fillStyle = "rgb(109, 105, 75)";
		ctx.fill();
		ctx.restore();
	}
    function drawChannelCanvasSchools(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(16.1, 90.3);
		ctx.bezierCurveTo(-0.4, 88.1, 0.1, 68.5, 0.1, 68.5);
		ctx.lineTo(16.6, 64.1);
		ctx.closePath();
		ctx.fillStyle = "rgb(0,0,0)";
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(1298.1, 90.3);
		ctx.bezierCurveTo(1314.6, 88.1, 1314.1, 68.5, 1314.1, 68.5);
		ctx.lineTo(1297.6, 64.1);
		ctx.closePath();
		ctx.fillStyle = "rgb(0,0,0)";
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(1315.4, 72.1);
		ctx.lineTo(1315.4, 4.0);
		ctx.bezierCurveTo(1315.4, 1.8, 1313.6, 0.0, 1311.4, 0.0);
		ctx.lineTo(4.0, 0.0);
		ctx.bezierCurveTo(1.8, 0.0, 0.0, 1.8, 0.0, 4.0);
		ctx.lineTo(0.0, 72.1);
		ctx.bezierCurveTo(0.0, 74.3, 20.4, 55.0, 660.4, 53.1);
		ctx.bezierCurveTo(1300.4, 51.1, 1315.4, 74.3, 1315.4, 72.1);
		ctx.closePath();
		ctx.fillStyle = '#004500';
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(20.4, 7.0);
		ctx.lineTo(1292.4, 7.0);
		ctx.bezierCurveTo(1294.6, 7.0, 1296.4, 8.8, 1296.4, 11.0);
		ctx.lineTo(1296.4, 52.4);
		ctx.bezierCurveTo(1296.4, 54.6, 1294.6, 56.4, 1292.4, 56.4);
		ctx.bezierCurveTo(1292.4, 56.4, 974.2, 46.4, 656.1, 46.4);
		ctx.bezierCurveTo(338.2, 46.4, 20.4, 56.4, 20.4, 56.4);
		ctx.bezierCurveTo(18.2, 56.4, 16.4, 54.6, 16.4, 52.4);
		ctx.lineTo(16.4, 11.0);
		ctx.bezierCurveTo(16.4, 8.8, 18.2, 7.0, 20.4, 7.0);
		ctx.closePath();
		ctx.fillStyle = "rgb(0,0,0)";
		ctx.fill();
		ctx.restore();
	}
    function drawChannelCanvasCrestline(ctx) {
		// layer1/Path
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(16.1, 90.3);
		ctx.bezierCurveTo(-0.4, 88.1, 0.1, 68.5, 0.1, 68.5);
		ctx.lineTo(16.6, 64.1);
		ctx.closePath();
		ctx.fillStyle = "rgb(0, 128, 0)";
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(1298.1, 90.3);
		ctx.bezierCurveTo(1314.6, 88.1, 1314.1, 68.5, 1314.1, 68.5);
		ctx.lineTo(1297.6, 64.1);
		ctx.closePath();
		ctx.fillStyle = "rgb(0, 128, 0)";
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(1315.4, 72.1);
		ctx.lineTo(1315.4, 4.0);
		ctx.bezierCurveTo(1315.4, 1.8, 1313.6, 0.0, 1311.4, 0.0);
		ctx.lineTo(4.0, 0.0);
		ctx.bezierCurveTo(1.8, 0.0, 0.0, 1.8, 0.0, 4.0);
		ctx.lineTo(0.0, 72.1);
		ctx.bezierCurveTo(0.0, 74.3, 20.4, 55.0, 660.4, 53.1);
		ctx.bezierCurveTo(1300.4, 51.1, 1315.4, 74.3, 1315.4, 72.1);
		ctx.closePath();
		ctx.fillStyle = '#004500';
		ctx.fill();

		// layer1/Path
		ctx.beginPath();
		ctx.moveTo(20.4, 7.0);
		ctx.lineTo(1292.4, 7.0);
		ctx.bezierCurveTo(1294.6, 7.0, 1296.4, 8.8, 1296.4, 11.0);
		ctx.lineTo(1296.4, 52.4);
		ctx.bezierCurveTo(1296.4, 54.6, 1294.6, 56.4, 1292.4, 56.4);
		ctx.bezierCurveTo(1292.4, 56.4, 974.2, 46.4, 656.1, 46.4);
		ctx.bezierCurveTo(338.2, 46.4, 20.4, 56.4, 20.4, 56.4);
		ctx.bezierCurveTo(18.2, 56.4, 16.4, 54.6, 16.4, 52.4);
		ctx.lineTo(16.4, 11.0);
		ctx.bezierCurveTo(16.4, 8.8, 18.2, 7.0, 20.4, 7.0);
		ctx.closePath();
		ctx.fillStyle = "rgb(0, 128, 0)";
		ctx.fill();
		ctx.restore();
	}
	function drawCanvasB(context) {
		// layer1/Path
		context.beginPath();
		context.moveTo(1315.4, 72.1);
		context.lineTo(1315.4, 4.0);
		context.bezierCurveTo(1315.4, 1.8, 1313.6, 0.0, 1311.4, 0.0);
		context.lineTo(4.0, 0.0);
		context.bezierCurveTo(1.8, 0.0, 0.0, 1.8, 0.0, 4.0);
		context.lineTo(0.0, 72.1);
		context.bezierCurveTo(0.0, 74.3, 20.4, 55.0, 660.4, 53.1);
		context.bezierCurveTo(1300.4, 51.1, 1315.4, 74.3, 1315.4, 72.1);
		context.closePath();
		context.fillStyle = '#004500';
		context.fill();
	}

	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

