	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			//sameHeightHome();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			//sameHeightHome();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			//sameHeightHome();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
				"imageHeight"		: 386,
				"imageControls"		: "off",
				"animateType"		: "fade", // CHOICES - "slide"
				"animateSpeed"		: 3,
				"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
				"autoRotate"		: "on",
				"useTitles"		: "on",
				"useCaptions"		: "on",
				"useOverlay"		: "off",
				"randomOrder"		: "off",
				"linkedElement"		: ['picture'], // CHOICES - ['picture'], ['title'], ['overlay']
				"itemizedBullets"	: "on",
				"thumbnails"		: "off",
				"thumbnailWidth"	: 50,
				"thumbnailHeight"	: 32,
				"thumbnailControls"	: "off",
				"thumbnailLimit"	: 3,
				"addClass"		: "unclassed",
				"moreLinkText"		: "off" 
			});

			//GALLERY DESC. BACKGROUND
			var rgbColor = "rgba("+hexToRgb('#004500')+",0.8)";
			$("#hp-slideshow .gallery-desc").css("background-color" , "rgbColor");
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		$(".hp.column.one div.ui-widget-header, .hp.column.two div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$(".ui-hp div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500);
		});
	}

	function accordionMenu(){
    	//var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

		//DOC make homepage apps collapsible for small mobile devices
		if(!$(".hp.column.three div.ui-widget.app.active").length){
			$(".hp.column.three div.ui-widget.app:first").addClass("active");
		}
		$(".hp.column.three div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");
		});
        $('.hp.column.three div.ui-widget-header').on('keydown', function(e) { 
        // Trigger the click event from the keyboard 
          var code = e.which; 
          // 13 = Return, 32 = Space 
          if ((code === 13) || (code === 32)) { 
            $("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");
          }

        });
	}

	function sameHeightHome() {
		if($("#hp-content").length) {
			var contentHeight = $("#hp-content").height();
			$(".hp.column.one .column-inner, .hp.column.two .column-inner").height(contentHeight); 

			$(".hp.column.one .ui-widget.app, .hp.column.two .ui-widget.app").resize(function() {
				$(".hp.column.one, .hp.column.two").height(contentHeight); 
			});
		}
	}	

