        window.resizeMiniCalendar20200429064818687 = function () {
        var calId = 'miniCal20200429064818687';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200429064818687';
        var calMonthFullId = 'miniCalMonthFull20200429064818687';
        var calMonthShortId = 'miniCalMonthShort20200429064818687';
        var calGoToCalFullId = 'miniCalGotToCalFull20200429064818687';
        var calGoToCalShortId = 'miniCalGoToCalShort20200429064818687';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

