    	// SWAP SPLASHPAGE THEME EACH WEEK
        Date.prototype.getWeek = function() {
            var onejan = new Date(this.getFullYear(), 0, 1);
            return Math.ceil((((this - onejan) / 86400000) + onejan.getDay() + 1) / 7);
        }

        var weekNumber = (new Date()).getWeek();

        if(weekNumber % 2 === 0) {
        	// EVEN WEEK - DISPLAY SPLASHPAGE THEME 2
            //$("#hp-splashpage-outer").addClass("splashpage-theme-two");
            $(splashTheme2).prependTo("#gb-page-outer");
        } else {
        	// ODD WEEK - DISPLAY SPLASHPAGE THEME 1
            //$("#hp-splashpage-outer").addClass("splashpage-theme-one");
            $(splashTheme1).prependTo("#gb-page-outer");
                setTimeout(function() {
                $(".splash-img.seven, .splash-img.eleven, .splash-img.sixteen, .splash-img.twentyone").fadeOut();
                //$(".splash-img.twentyfive").css("left","576px");
                $(".splash-img.twentyfive").animate({
                    "left": "576px"
                });
            },2500);

            setTimeout(function() {
                $(".splash-img.six, .splash-img.ten, .splash-img.seventeen, .splash-img.twentytwo").fadeOut();
            },2900);
        }

    	// SET COOKIE ON CLOSE
        if ($.cookie('hide-splashpage')) {
            $("#hp-splashpage-outer").remove();
            $("body").removeAttr("style");
        }

        $(".close-splash-btn, #hp-splashpage-outer").click(function() {
            $("#hp-splashpage-outer").remove();
            $("body").removeAttr("style");
            $.cookie('hide-splashpage', true);
        });
        $(".close-splash-btn").keydown(function (e) {
            if (e.keyCode == 13 || e.keyCode == 32) {
                e.preventDefault();

				$("#hp-splashpage-outer").remove();
                $("body").removeAttr("style");
            	$.cookie('hide-splashpage', true);
            }
            if (e.keyCode == 9) {
                e.preventDefault();
            }
        });

        $(document).on("keydown", function(e) {
        	// esc key
            if(e.keyCode == 27 && $("#hp-splashpage-outer").length) {
                e.preventDefault();

                $("#hp-splashpage-outer").remove();
                $("body").removeAttr("style");
                $.cookie('hide-splashpage', true);
            }
        });
    }

