	function addTranslate() {
		$("div#sw-mystart-left").append("
Translate Back To English");
		$(".sw-mystart-button.translateBack").hide();
		$("div.sw-mystart-button.translateBack").click(function () {
			$.cookies.del('swTranslationCookie');
		});
		if($.cookies.get('swTranslationCookie')) {
			var langCode = $.cookies.get('swTranslationCookie');
			$("body").translate("english", langCode,{
				toggle: true,
				fromOriginal:true,
				start: function(translation) {
					joelGrowl("Translating...", "translating",0);
				},
				complete: function(translation) {
					$(".joelGrowl.translating").text("translation complete!");
					$(".joelGrowl.translating").delay(1000).fadeOut('fast', function(){
						$(this).remove();
					});
				}
			});
			$("div.sw-mystart-button.translateBack").show();
		} else {
			$.translate(function(){
			    	$.translate.ui({
			    		filter: ["af", "sq", "ar", "be", "bg", "ca", "zh", "zh-CN", "zh-TW", "hr", "cs", "da", "nl", "et", "tl", "fi", "fr", "gl", "de", "el", "iw", "hi", "hu", "is", "id", "ga", "it", "ja", "ko", "lv", "lt", "mk", "ms", "mt", "no", "fa", "pl", "pt", "ro", "ru", "sr", "sk", "sl", "es", "sw", "sv", "tl", "th", "tr", "uk", "vi", "cy", "yi"]
			    	})
			    	.appendTo('div#sw-mystart-left');
			    	$(".sw-mystart-dropdown.translate").click(function() {
					$(".sw-dropdown",this).show();
				});
				$(".sw-mystart-dropdown.translate").hover(function() {
				},
				function(){
					$(".sw-dropdown",this).hide();
				});
				$(".jq-translate-ui li a").click(function() {
					var langCode = $(this).attr("id");
					$("body").translate("english", langCode,{
						toggle: true,
						fromOriginal:true,
						start: function(translation) {
							joelGrowl("Translating...", "translating",0);
						},
						complete: function(translation) {
							$(".joelGrowl.translating").text("translation complete!");
							$(".joelGrowl.translating").delay(1000).fadeOut('fast', function(){
								$(this).remove();
							});
						}
					})
					$(this).parent().parent().parent().parent().hide();
					$("div.sw-mystart-button.translateBack").show();
					$.cookies.set('swTranslationCookie', langCode);
				});
			});
		}
	}
	function searchText() {
		//Inital Value
		$("#sw-search-input").val("Search This Site...");
		//Clear the Value on Focus
		$("#sw-search-input").focus(function(){
			$(this).val("");
		});
		//If Search Value is left Blank, reinsert Inital Value
		$("#sw-search-input").blur(function(){
			if($(this).val() == ""){
				$("#sw-search-input").val("Search This Site..."); 
			}
		});
	}
	function rotatingHeadlines() {
		if(customHeadlineApp == "on"){
			//FIRST VARIABLE IS TIMING, SECOND IS FADE SPEED
			$("#sw-content-container2.region.ui-hp div.ui-widget.app.headlines").simpleHeadlines({
				"delay"			: 8,
				"fadeSpeed"		: 2,
				"useTitles"		: "yes",
				"useDescriptions"	: "yes",
				"useLinks"		: "yes",
				"useLiOverlays"		: "yes",
				"controls"		: "yes",
				"removeStyles"		: "yes"
			});
			$("#sw-content-container2 div.ui-widget.app.headlines div.ui-widget-header").appendTo($("#sw-content-container2 div.joey-headline-container"));
		}else{
			//Do Use the Custom App
		}
	}
	function colorChange() {
		//Primary Color
		$("div#gb-page-accent").css("background-color", primaryColor);
		$("div#gb-header").css("background-color", primaryColor);
		$("div.hp.column.two div.ui-widget.app div.ui-widget-header, #sw-content-container2 div.joey-headline-container div.ui-widget-header").css("background-color", primaryColor);

		//Secondary Color
		$("div#gb-channel-list-outer").css("background-color", secondaryColor);
		$("div#hp-slideshow-outer").css("background-color", secondaryColor);

		//Accent Color
		$("div.hp.column.three").css("background-color", accentColor);
		$("div.sp.column.one").css("background-color", accentColor);

		//Background Color
		$("div#gb-page-outer, body").css("background-color", backgroundColor);

	}
	function contentHeaders() {     
        	$("div.hp.column.one div.ui-widget.app div.ui-widget-header, div.hp.column.three div.ui-widget.app div.ui-widget-header").each(function(){
		 	$(this).addClass("ui-clear");
		 	var str = $("h1",this).text();
		 	var arr = str.split(" ");
		 	$("h1", this).css("display", "none");
		 	if(!arr[1]){
			 	$(this).append("
" + arr[0] + "");
			} else {
			 	$(this).append("
" + arr[0] + "");
			 	var arrString = "";
			 	for(j=1;j<arr.length;j++){
			 		if(j==1){
			 			arrString = arrString + arr[j]
			 		} else {
			 			arrString = arrString + " " + arr[j];
			 		}
			 	}
			 	$(this).append("
" + arrString + "");
			}
		});
	}
