(function() {
	var w = window, d = document;
	var s = d.createElement('script');
	s.setAttribute('async', 'true');
	s.setAttribute('type', 'text/javascript');
	s.setAttribute('src', '//c1.rfihub.net/js/tc.min.js');
	var f = d.getElementsByTagName('script')[0];
	f.parentNode.insertBefore(s, f);
	if (typeof w['_rfi'] !== 'function') {
		w['_rfi']=function() {
			w['_rfi'].commands = w['_rfi'].commands || [];
			w['_rfi'].commands.push(arguments);
		};
	}
	_rfi('setArgs', 'ver', '9');
	_rfi('setArgs', 'rb', '32755');
	_rfi('setArgs', 'ca', '20784391');
	_rfi('setArgs', '_o', '32755');
	_rfi('setArgs', '_t', '20784391');
	_rfi('track');
})();

$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=ZrK5rtLeyUeuG1NhC%2fNGoQ%3d%3d&SessionID=45ab2c1a-c0f2-4327-b400-6e2be6a841a7&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+5%3a54%3a56+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.77.153';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
