    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "HeaderIsSticky": false,
        "UseCustomTranslator": true,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            // USED BY THE CUSTOM GOOGLE TRANSLATE FUNCTIONALITY
            if(this.UseCustomTranslator) {
            	//console.log(this.readCookie("CreativeRedirectTo"));
                if(!this.readCookie("CreativeRedirectTo")) {
                    var pageURL = window.location.href.split("#");
                    this.createCookie("CreativeRedirectTo", pageURL[0]);
                }
            }

            this.SetTemplateProps();
            this.DynamicStyleSheet();
            this.DynamicBackgrounds();
            this.JsMediaQueries();
            this.Header();
            this.MyStart();
            this.GoogleTranslate();
            this.Slideshow();            
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.StreamingVideo();
            this.RsMenu();
            this.AppAccordion();
            this.Search();
            this.Canvas();

            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            // USED BY THE CUSTOM GOOGLE TRANSLATE FUNCTIONALITY
            if(this.UseCustomTranslator) {
                var pageURL = window.location.href.split("#");
                this.createCookie("CreativeRedirectTo", pageURL[0]);
            }
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.StickyHeader();
            this.ChannelBar();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyHeader();            
        },

       "WindowScroll": function() {
            this.StickyHeader();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    $(".hp-row.two .headlines").each(function() {
                    	if($(".ui-widget-detail ul", this).length == 1) {
                            $(".ui-widget-detail", this).prepend('');
                            $(".ui-articles:eq(1) li:nth-child(odd)", this).appendTo($(".ui-articles:eq(0)", this));
                        }
                    });                   
                break;
                case "768":
                case "640":
                case "480":
                case "320":
                    $(".hp-row.two .headlines").each(function() {
                    	if($(".ui-widget-detail ul", this).length == 2) {
                            $(".ui-articles:eq(1) li", this).appendTo($(".ui-articles:eq(0)", this));
                            $(".ui-articles:eq(1)", this).remove();
                        }
                    });                     
                break;
            }
        },

        "Canvas": function() {
        	//Variables
            var regionCBGSrc = $.trim('/cms/lib/CA01902727/Centricity/Template/GlobalAssets/images///backgrounds/2018 El Sereno graduation - RR-14.jpg');
            var srcCheck = regionCBGSrc.indexOf("Faces/default-man.jpg") > -1;
            if((regionCBGSrc == "") || (srcCheck)) {
                regionCBGSrc = '/cms/lib/CA01902727/Centricity/Template/24/hp-background-one.jpg';
            }
            var regionCBGOpacityToggle = true;
            var regionCBGOpacity = '10';
            if(regionCBGOpacityToggle == false || regionCBGOpacity == "100") {
            	regionCBGOpacity = 1;
            } else if (regionCBGOpacity == "15 - Default") {
            	regionCBGOpacity = 0.15;
            } else {
            	regionCBGOpacity = "0."+regionCBGOpacity;
                regionCBGOpacity = Number(regionCBGOpacity);
            }

            var regionDBGSrc = $.trim('/cms/lib/CA01902727/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');
            var srcCheck2 = regionDBGSrc.indexOf("Faces/default-man.jpg") > -1;
            if((regionDBGSrc == "") || (srcCheck2)) {
                regionDBGSrc = '/cms/lib/CA01902727/Centricity/Template/24/hp-background-two.jpg';
            }
            var regionDBGOpacityToggle = true;
            var regionDBGOpacity = '0';
            if(regionDBGOpacityToggle == false || regionDBGOpacity == "100") {
            	regionDBGOpacity = 1;
            } else if (regionDBGOpacity == "30 - Default") {
            	regionDBGOpacity = 0.30;
            } else {
            	regionDBGOpacity = "0."+regionDBGOpacity;
                regionDBGOpacity = Number(regionDBGOpacity);
            }

            if($("#hp-slideshow").length) {
                var mmgCanvas = document.getElementById("mmg-canvas");
                var mmgCTX = mmgCanvas.getContext("2d");
                drawMMGCanvas(mmgCTX);

                function drawMMGCanvas(ctx) {
                    ctx.save();
                    ctx.beginPath();
                    ctx.moveTo(0.0, 0.0);
                    ctx.bezierCurveTo(0.0, 0.0, 357.9, 24.5, 717.2, 24.5);
                    ctx.bezierCurveTo(1079.4, 24.5, 1443.0, 0.0, 1443.0, 0.0);
                    ctx.lineTo(1443.0, 131.0);
                    ctx.bezierCurveTo(1443.0, 131.0, 1078.9, 151.0, 716.5, 151.0);
                    ctx.bezierCurveTo(357.4, 151.0, 0.0, 131.0, 0.0, 131.0);
                    ctx.lineTo(0.0, 0.0);
                    ctx.closePath();
                    ctx.fillStyle = "rgb(255, 255, 255)";
                    ctx.fill("evenodd");
                    ctx.restore();
                }
            }

            if($(".ie11").length && $(".hp").length || $(".edge").length && $(".hp").length) {
                var oneCanvas = document.getElementById("hp-row-one-canvas");
                var oneCTX = oneCanvas.getContext("2d");
                drawHPRowOneCanvas(oneCTX);

                function drawHPRowOneCanvas(ctx) {
                    ctx.save();
                    ctx.beginPath();
                    ctx.moveTo(0.0, 0.0);
                    ctx.lineTo(1441.0, 0.0);
                    ctx.lineTo(1441.0, 909.8);
                    ctx.bezierCurveTo(1441.0, 909.8, 1081.3, 941.7, 720.9, 941.7);
                    ctx.bezierCurveTo(360.8, 941.7, 0.0, 909.8, 0.0, 909.8);
                    ctx.lineTo(0.0, 0.0);
                    ctx.closePath();
                    ctx.fillStyle = '#445F3F';
                    ctx.fill("evenodd");
                    ctx.restore();
                }

                var twoCanvas = document.getElementById("hp-row-two-canvas");
                var twoCTX = twoCanvas.getContext("2d");
                drawHPRowTwoCanvas(twoCTX);

                function drawHPRowTwoCanvas(ctx) {
                    ctx.save();
                    ctx.beginPath();
                    ctx.moveTo(0.0, 0.0);
                    ctx.lineTo(1441.0, 0.0);
                    ctx.lineTo(1441.0, 909.8);
                    ctx.bezierCurveTo(1441.0, 909.8, 1081.3, 941.7, 720.9, 941.7);
                    ctx.bezierCurveTo(360.8, 941.7, 0.0, 909.8, 0.0, 909.8);
                    ctx.lineTo(0.0, 0.0);
                    ctx.closePath();
                    ctx.fillStyle = '#FFFFFF';
                    ctx.fill("evenodd");

                    //Load image
                    var img = new Image();   // Create new img element
                    img.addEventListener('load', function() {
                        ctx.clip();
                        ctx.globalAlpha = regionCBGOpacity;
                        ctx.drawImage(img, 0, 37);
                        ctx.restore();
                    }, false);
                    img.src = regionCBGSrc; // Set source path
                }

                var threeCanvas = document.getElementById("hp-row-three-canvas");
                var threeCTX = threeCanvas.getContext("2d");
                drawHPRowThreeCanvas(threeCTX);

                function drawHPRowThreeCanvas(ctx) {
                    ctx.save();
                    ctx.beginPath();
                    ctx.moveTo(0.0, 0.0);
                    ctx.lineTo(1441.0, 0.0);
                    ctx.lineTo(1441.0, 909.8);
                    ctx.bezierCurveTo(1441.0, 909.8, 1081.3, 941.7, 720.9, 941.7);
                    ctx.bezierCurveTo(360.8, 941.7, 0.0, 909.8, 0.0, 909.8);
                    ctx.lineTo(0.0, 0.0);
                    ctx.closePath();
                    ctx.fillStyle = '#002a4a';
                    ctx.fill("evenodd");

                    //Load image
                    var img = new Image();   // Create new img element
                    img.addEventListener('load', function() {
                        ctx.clip();
                        ctx.globalAlpha = regionDBGOpacity;
                        ctx.drawImage(img, 0, 37);
                        ctx.restore();
                    }, false);
                    img.src = regionDBGSrc; // Set source path
                }
            }
   		},

        "StickyHeader": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("header").outerHeight();
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.HeaderIsSticky) {
                                $("header").removeClass("fixed-element").removeAttr("style");
                                $("#gb-page").css("padding-top", 0);

                                this.HeaderIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("header").css("height", $("#gb-header-wrapper").outerHeight(true));
                            $("#gb-page").css("padding-top", $("#gb-header-wrapper").outerHeight(true));

                            if(!this.HeaderIsSticky) {
                                $("header").addClass("fixed-element");

                                this.HeaderIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("nav").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.HeaderIsSticky) {
                            $("header").removeClass("fixed-element").removeAttr("style");
                            $("#gb-page").css("padding-top", 0);

                            this.HeaderIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

