        "MyStartDropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(document).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(document).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function() {
                            	if($(dropdownList + " .goog-te-combo", this).length) {
                                	$(dropdownList + " .goog-te-combo", this).focus();
                                } else if($(this).attr("id") == "cs-schools-list") {
                                	console.log("schools");
                                	$(".gb-school-list-tab.active").focus();
                                } else {
                                	$(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                                }
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if($(this).attr("aria-controls") != "cs-schools-list") {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(document).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
     				case template.KeyCodes.esc:
                    	e.preventDefault();

                    	// CLOSE MENU
                    	$(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parents(dropdownParent).find(dropdownSelector).focus().attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
					break;
                }
            });

            $(dropdownParent).mouseleave(function() {
				$(dropdownList + " a", this).attr("tabindex", "-1");
				$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

       "GoogleTranslate": function() {
            //For scope
            var _this = this;
            // ========== CUSTOM GOOGLE TRANSLATOR PROJECT BUILT BY BRENTON KELLY ========== //

            if(this.UseCustomTranslator) {
                $("body").addClass("cs-custom-translator");

                var googleLangs = [["Afrikaans","af"],["Albanian","sq"],["Arabic","ar"],["Armenian","hy"],["Azerbaijani","az"],["Basque","eu"],["Belarusian","be"],["Bengali","bn"],["Bosnian","bs"],["Bulgarian","bg"],["Catalan","ca"],["Cebuano","ceb"],["Chichewa","ny"],["Chinese Simplified","zh-CN"],["Chinese Traditional","zh-TW"],["Croatian","hr"],["Czech","cs"],["Danish","da"],["Dutch","nl"],["Esperanto","eo"],["Estonian","et"],["Filipino","tl"],["Finnish","fi"],["French","fr"],["Galician","gl"],["Georgian","ka"],["German","de"],["Greek","el"],["Gujarati","gu"],["Haitian Creole","ht"],["Hausa","ha"],["Hebrew","iw"],["Hindi","hi"],["Hmong","hmn"],["Hungarian","hu"],["Icelandic","is"],["Igbo","ig"],["Indonesian","id"],["Irish","ga"],["Italian","it"],["Japanese","ja"],["Javanese","jw"],["Kannada","kn"],["Kazakh","kk"],["Khmer","km"],["Korean","ko"],["Lao","lo"],["Latin","la"],["Latvian","lv"],["Lithuanian","lt"],["Macedonian","mk"],["Malagasy","mg"],["Malay","ms"],["Malayalam","ml"],["Maltese","mt"],["Maori","mi"],["Marathi","mr"],["Mongolian","mn"],["Myanmar (Burmese)","my"],["Nepali","ne"],["Norwegian","no"],["Persian","fa"],["Polish","pl"],["Portuguese","pt"],["Punjabi","pa"],["Romanian","ro"],["Russian","ru"],["Serbian","sr"],["Sesotho","st"],["Sinhala","si"],["Slovak","sk"],["Slovenian","sl"],["Somali","so"],["Spanish","es"],["Sundanese","su"],["Swahili","sw"],["Swedish","sv"],["Tajik","tg"],["Tamil","ta"],["Telugu","te"],["Thai","th"],["Turkish","tr"],["Ukrainian","uk"],["Urdu","ur"],["Uzbek","uz"],["Vietnamese","vi"],["Welsh","cy"],["Yiddish","yi"],["Yoruba","yo"],["Zulu","zu"]];
                // CHECK TO MAKE SURE THAT LANGUAGE OPTIONS EXIST IN THE translate-setup.txt FILE BEFORE TRYING TO DO ANYTHING
                if (typeof csLangOptions !== "undefined" || csLangOptions !== null) {
                    if(csLangOptions.length) {
                        // ADD A GOOGLE TRANSLATE ELEMENT ALONG WITH A CLONED GOOGLE TRANSLATE ELEMENT

                        // LAZY LOAD THE TRANSLATE SCRIPT SO WE ARE SURE THAT google_translate_element exists
                        _this.loadScript("//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit", googleTranslateElementInit);

                        // BUILD OUT THE CUSTOM TRANSLATE LINKS FROM THE translate-setup.txt FILE
                        var csLangs = '';
                        for(var i in csLangOptions) {
                            csLangs += '
' + csLangOptions[i][0] + '';
                        }
                        csLangs += '';

                        // ADD THE CUSTOM TRANSLATE LINKS TO THE DROPDOWN
                        $(".google-translate-element").append(csLangs);

                        // FIND THE CUSTOM GOOGLE TRANSLATE ACTIVE BLOCK AND REPLACE WITH LINKS
                        var csGoogleTransLinks = '
English';
                        for(var l in csLangOptions) {
                            if(csLangOptions[l][0] != "Other") {
                                csGoogleTransLinks += '
' + csLangOptions[l][0] + '';
                            }
                        }
                        csGoogleTransLinks += '';

                        $(".ui-widget.app .ui-widget-detail:contains('[$google-translate-links$]')").addClass("cs-google-translate-links");
                        if($(".cs-google-translate-links").length) {
                            $(".cs-google-translate-links").each(function() {
                                var stripBlock = $(this).html().replace(/\[\$google-translate-links\$\]/ig, csGoogleTransLinks);
                                $(this).html(stripBlock);
                            });
                        }

                        // FIND THE CUSTOM GOOGLE TRANSLATE ALL LINKS ACTIVE BLOCK AND REPLACE WITH FULL LIST OF LINKS
                        var csGoogleTransAllLinks = '
Select Language
English';
                        for(var a in googleLangs) {
                            csGoogleTransAllLinks += '
' + googleLangs[a][0] + '';
                        }
                        csGoogleTransAllLinks += '';

                        $(".ui-widget.app .ui-widget-detail:contains('[$google-translate-all-links$]')").addClass("cs-google-translate-all-links");
                        if($(".cs-google-translate-all-links").length) {
                            $(".cs-google-translate-all-links").each(function() {
                                var stripBlock = $(this).html().replace(/\[\$google-translate-all-links\$\]/ig, csGoogleTransAllLinks);
                                $(this).html(stripBlock);
                            });
                        }

                        // BIND THE CUSTOM DROPDOWN CLICK EVENT
                        $(".google-translate-element").click(function(e) {
                            $("ul", this).slideToggle(300, "swing");
                        });
                        $(".google-translate-element").hover(function(){}, function() {
                            $("ul", this).slideUp(300, "swing");
                        });
                    }
                }
            } else {
                // GOOGLE BRANDED REGULAR TRANSLATOR 
                // GO AHEAD AND REMOVE ANY OF OUR [$google-translate-links$] ACTIVE BLOCKS IN CASE THERE ARE ANY FLOATING AROUND OUT THERE FROM THE CUSTOM TRANSLATOR IMPLEMENTATION
                $(".ui-widget.app .ui-widget-detail:contains('[$google-translate-links$]')").addClass("cs-google-translate-links");
                if($(".cs-google-translate-links").length) {
