		"SchoolListManaganment": function() {
        	//For scope
            var _this = this;

            var schools = '', sortedSchools = {};

            // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED TYPES IN THE ORDER WE WANT
            var expectedNameOrder = ["elementary", "k-8", "middle", "high", "other"];
            schoolList.sort(function(a, b) {
                // WE WANT THE SPECIFIC CATEGORIES ABOVE TO COME FIRST AND IN THE SPECIFIED ORDER
                // WE'LL CAPTURE THOSE IN THIS FIRST CHECK
                if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > -1 && expectedNameOrder.indexOf(b.schoolType.toLowerCase()) > -1) {
                    if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                        return -1;
                    }
                    if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                        return 1;
                    }
                }

                // HERE, THE SCHOOL CATEGORY DOESN'T EXIST IN THE CATEGORY ARRAY ABOVE
                else {
                    if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                        return -1;
                    }
                    if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                        return 1;
                    }
                }

                // NEITHER CHECK APPLIES SO WE JUST LEAVE THIS IN IT'S CURRENT POSITION
                return 0;
            });

            // SORT THE SCHOOLS INTO TYPE OBJECTS
            for(var type in schoolList) {
                if(schoolList[type].schoolVisible.toLowerCase() == 'true') {
                    var thisType = schoolList[type].schoolType.toLowerCase();

                    // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                    if(schoolList[type].schoolType.indexOf(",") > -1) {
                        var subTypes = schoolList[type].schoolType.split(",");

                        // LOOP THROUGH THE SUB TYPES
                        for(var subType in subTypes) {
                            var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                            // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                            if(!sortedSchools.hasOwnProperty(thisSubType)) {
                                // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                sortedSchools[thisSubType] = [schoolList[type]];
                            } else {
                                // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                sortedSchools[thisSubType].push(schoolList[type]);
                            }
                        }
                    }

                    // SINGLE TYPE VALUE
                    else {
                        // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                        if(!sortedSchools.hasOwnProperty(thisType)) {
                            // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                            sortedSchools[thisType] = [schoolList[type]];
                        } else {
                            // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                            sortedSchools[thisType].push(schoolList[type]);
                        }
                    }
                }
            }

            // BUILD THE SCHOOL MENU STRUCTURE
            var schoolMenuTabs = '
';
            var schoolMenuLists = '
';
            var i = 0;

            $.each(sortedSchools, function(type, schools) {
                schoolMenuLists += '
';
                var typeName = type;

                // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
                schools.sort(function(a, b) {
                    if(a.schoolName > b.schoolName) {
                        return 1;
                    }
                    if(a.schoolName < b.schoolName) {
                        return -1;
                    }
                    return 0;
                });

                // BUILD HEADER
                if(type == "k-8") {
                    schoolMenuTabs += '

' + type + '';
                } else {
                    schoolMenuTabs += '

' + type + ' schools';
                }

                // BUILD LIST
                for(var school in schools) {
                    if(schools[school].schoolVisible.toLowerCase() == 'true') {
                        schoolMenuLists += '
' + schools[school].schoolName + '';
                    }
                }

                schoolMenuLists += '';
                i++;
            });

            schoolMenuTabs += "";
            schoolMenuLists += '';

            $(".schools .cs-dropdown").html(schoolMenuTabs+schoolMenuLists);
            $(".gb-school-list-tab").eq(0).addClass("active").attr({"aria-selected": true, "tabindex": 0});
            $(".gb-school-list").eq(0).addClass("active").attr("aria-hidden", false);

            $(document).on("click", ".gb-school-list-tab", function(e) { 
                if(_this.AllyClick(e)) {
                    $(".gb-school-list-tab").removeClass("active").attr({"aria-selected": false, "tabindex": -1});
                    $(this).addClass("active").attr({"aria-selected": true, "tabindex": 0});

                    $(".gb-school-list").removeClass("active").attr("aria-hidden", true);
                    $(".gb-school-list").eq($(this).index()).addClass("active").attr("aria-hidden", false);
                }
            });

            $(document).on("keydown", ".gb-school-list-tab", function(e) {                        
                // CAPTURE KEY CODE
                switch(e.keyCode) {                            
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.enter:
                    case _this.KeyCodes.space:
                        e.preventDefault();

                        $(".gb-school-list-tab").removeClass("active").attr({"aria-selected": false, "tabindex": -1});
                        $(this).addClass("active").attr({"aria-selected": true, "tabindex": 0});

                        $(".gb-school-list").removeClass("active").attr("aria-hidden", true);
                        $(".gb-school-list").eq($(this).index()).addClass("active").attr("aria-hidden", false);
                        $(".gb-school-list.active li:eq(0) a").focus();
                    break;

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                        e.preventDefault();

                        $(".gb-school-list.active li:eq(0) a").focus();
                    break;

                    // CONSUME ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();

                        if($(this).index() == 0) {
                            $(this).siblings().last().focus().click();
                        } else {
                            $(this).prev().focus().click();
                        }
                    break;

                    case _this.KeyCodes.down:
                    case _this.KeyCodes.right:
                        e.preventDefault();

                        if($(this).index() == $(this).siblings().length) {
                            $(this).siblings().first().focus().click();
                        } else {
                            $(this).next().focus().click();
                        }
                    break;
                }
            });
        },

