        "Search": function() {
            // FOR SCOPE
            var _this = this;   

        	//Variables
            var placeholder = "Search";

            $("#gb-search-input").focus(function() {
                if($(this).val() == placeholder) {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val(placeholder);
                }
            });

            //Show/hide search
            $(document).on("click keydown", "#gb-search-menu-button", function(e) {            
                if(_this.AllyClick(e)) {
                	e.preventDefault();
                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search-form").attr("aria-hidden", "false").show("slide", { direction: "right" }, 250);

                        setTimeout(function() {
                            $("#gb-search-input").focus();
                        }, 260);
                    } else {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 250);
                    }
                }
            });

            // FORM SUMBIT
            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();
            	if($("#gb-search-input").val() != "" && $("#gb-search-input").val() != placeholder) {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

           $(document).on("keydown", "#gb-search-input", function(e) {                        
                // CAPTURE KEY CODE
                switch(e.keyCode) {                            
                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	e.preventDefault();
                        $("#gb-search-menu-button").focus();
                    break;
                }
            });
        },

        /* General Methods */
        "AllyClick": function(event) {
            if(event.type == "click") {
            	event.preventDefault();
                return true;
            } else if(event.type == "keypress" || event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
            	event.preventDefault();
                return true;
            } else {
                return false;
            }
        },

        "loadScript": function (url, callback){
                var script = document.createElement("script")
                script.type = "text/javascript";

                if (script.readyState){  // IE
                        script.onreadystatechange = function(){
                                if (script.readyState == "loaded" || script.readyState == "complete"){
                                        script.onreadystatechange = null;
                                }
                        }
                } else {  // Others
                        script.onload = function(){}
                }

                script.src = url;
                document.getElementsByTagName("head")[0].appendChild(script);
        },

        "createCookie": function(name, value, days) {
            var expires;

            if (days) {
                var date = new Date();
                date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                expires = "; expires=" + date.toGMTString();
            } else {
                expires = "";
            }
            document.cookie = escape(name) + "=" + escape(value) + expires + "; path=/";
        },

        "readCookie": function(name) {
            var nameEQ = escape(name) + "=";
            var ca = document.cookie.split(';');
            for (var i = 0; i < ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                if (c.indexOf(nameEQ) === 0) return unescape(c.substring(nameEQ.length, c.length));
            }
            return null;
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+','+g+','+b;
        },

        "LoadImage": function(element, image, defaultImage, altText, toggle) {
        	//Variables
            image = $.trim('/cms/lib/CA01902727/Centricity/Template/GlobalAssets/images///Logos/san-juan_logo.png');

            if(toggle) {
            	var srcCheck = image.indexOf("Faces/default-man.jpg") > -1;

                if((image != "") && (!srcCheck)) {
                    $(element).append('');
                } else {
                    $(element).append('');
                }
            }

        }
    };

