$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=8RKb5Id9Ll57%2fM9%2bB6Fi5Q%3d%3d&SessionID=843ab981-2179-456a-bddd-ce10871e3d40&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+4%3a35%3a09+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.84.145';
