var newsxhttp = new XMLHttpRequest();
newsxhttp.onreadystatechange = function() {
    if (this.readyState == 4 && this.status == 200) {
        myNewsFunction(this);
    }
};
newsxhttp.open("GET", "https://www.csdnb.org/RSS/moreNewsFour.xml", true);
newsxhttp.send();
