   	$(document).ready(function() {

   	// Cache popup so the it won't appear when the user refreshes the page

   	if(localStorage.getItem('popState') != 'shown'){

   	$("#modalBox").fadeIn();
   	var id = '#modalBox';

   	// Get the window height and width

   	var winH = $(window).height();
   	var winW = $(window).width();

   	// Set the popup window to center

   	$("id").css({
   	"top": "0",
   	"left": "0",
   	"right": "0",
   	"bottom": "0",
   	"margin": "0 auto"
   	});

   	// Cover the whole website

   	if ($("#modalBox").css('display') == 'block') {
   	$('#modalWrapper').css({"position":"absolute"});
   	}

   	// Accessibility
   	
   	$(document).keydown(function(e) {
   	if (e.keyCode == 27) {
   	$('.window').fadeOut();
   	$('#modalWrapper').css({"position":"static"});
   	}

   	$("input[name='checkbox_alert']").keydown(function(e) {
   	if (e.keyCode == 13) {
   	$(this).prop( "checked", true );
   	}
   	})	

   	});

   	$('.modalClose').click(function (e) {
   	e.preventDefault();

   	if ($("input[name='checkbox_alert']").is(":checked")) {
   	$('.window').fadeOut();
   	$('#modalWrapper').css({"position":"static"});
   	localStorage.setItem('popState','shown');
   	} else {
   	$(".checkbox").css("background", "rgba(178, 50, 50, 0.2)");
   	$("input[name='checkbox_alert']").focus();
   	}
   	});

   	}/* closing if */


   	});

