	function buildAwards(){
		var awardsCheck = 'false';

		if(awardsCheck == "true") {
			var parsed = [];
			$.each(awards, function(i,v){
				if(v.name != "default") {
					parsed.push(v);
				}
			});
			if(parsed.length > 0) {
				var col1 = Math.ceil(parsed.length / 2),
				    col2 = parsed.length - col1;
				$.each(parsed, function(i,v){
					var $item = $("
" + v.name + "
");
					if(i < col1) {
						$item.prependTo("#hp-awards .one");
					} else {
						$item.appendTo("#hp-awards .three");
					}
					$item.clone().appendTo("#full-width-awards");
				});
			}

			$("#hp-awards").removeClass("hidden");
		}
	}

	function pgScreenSize(){
		var getWidth = $(window).width();
		marginPage(getWidth);
		awardClickable(getWidth);
		if (getWidth >= 1024) { // 960+
			photoGallery();	
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			//appView(); - Adam - 11.15.17 - #02966352
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			//appView(); - Adam - 11.15.17 - #02966352
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			//appView(); - Adam - 11.15.17 - #02966352
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			//appView(); - Adam - 11.15.17 - #02966352
		}
	}

	function photoGallery(){
		head.ready("photoGallery", function(){
			if(!$("#sw-content-container10.region.ui-hp .gallery-container").length){
				// PHOTO GALLERY CONFIGURATION
				$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
					"imageHeight"		: 270,
					"imageControls"		: "on",
					"animateType"		: "fade", // CHOICES - "slide"
					"animateSpeed"		: 3,
					"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
					"autoRotate"		: "on",
					"useTitles"		: "on",
					"useCaptions"		: "on",
					"useOverlay"		: "off",
					"randomOrder"		: "off",
					"linkedElement"		: ['picture'], // CHOICES - ['picture'], ['title'], ['overlay']
					"itemizedBullets"	: "off",
					"thumbnails"		: "off",
					"thumbnailWidth"	: 50,
					"thumbnailHeight"	: 32,
					"thumbnailControls"	: "off",
					"thumbnailLimit"	: 3,
					"addClass"		: "ui-clear",
					"moreLinkText"		: "Read more>>" 
				});
			}
		});
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

        function marginPage(w){
        	if(w >= 1024) {
			var margin = $("#gb-header-outer").outerHeight(true);
			$("#gb-page").css("margin-top", margin + "px");
		} else {
			$("#gb-page").css("margin-top", 0);
		}
        }

        function awardClickable(w){
        	if(w <= 480) {
        		$("#hp-awards").addClass("clickable");
        	} else {
        		$("#hp-awards").removeClass("clickable");
        	}
        }

