	$(document).ready(function() {
		//encode filepath as suggested by Google and set src attribute of iframe to that encoded url
        var filePath = "https://www.crk12.org//cms/lib/DE01903180/Centricity/Domain/4/Q_A_Superintendents_Message_rev.pdf";
        
        if (filePath !== ''){
        	$("#doc-viewer-text-6494").removeClass("ui-helper-hidden");
        }
		
        filePath = encodeURIComponent(filePath);
       
        var url = "https://docs.google.com/viewer?url=" + filePath + "&embedded=true";
        
        $("#doc-viewer-6494").attr("src", url);
	});

