	$(document).ready(function() {
		//encode filepath as suggested by Google and set src attribute of iframe to that encoded url
        var filePath = "https://www.crk12.org//cms/lib/DE01903180/Centricity/Domain/4/Corona Virus/Coronavirus_FAQ_State_of_Delaware_03112020.pdf";
        
        if (filePath !== ''){
        	$("#doc-viewer-text-6281").removeClass("ui-helper-hidden");
        }
		
        filePath = encodeURIComponent(filePath);
       
        var url = "https://docs.google.com/viewer?url=" + filePath + "&embedded=true";
        
        $("#doc-viewer-6281").attr("src", url);
	});

