            function jssInit_slider(event) {
                jQuery("#slider .jss-title > *").css("opacity", "0");

            }
            function jssInitEnd_slider(event) {
                jssAnimText_slider(event.item.index);
            }
            function jssAnimTextExec_slider(c, x, c2, x2) {
                if (c == undefined) { c = c2; x = x2; c2 = undefined; x2 = undefined; }
                jQuery(c).addClass(x + " animated")
                    .one(jQuery.support.animation.end, function(e) {
                        jQuery(this).removeClass(x + " animated");
                        jQuery(this).css("opacity", "1");
                        if (c2 != undefined) jssAnimTextExec_slider(c2, x2);
                });
            }
            function jssAnimText_slider(i) {
                if (!jQuery.support.animation || !jQuery.support.transition) {
                    return;
                }
                jssAnimTextExec_slider("#slider .owl-item:eq(" + i + ") .jss-title > *","bounceInLeft",undefined,undefined);
            }var slider = null;

