        items: 1,
        mouseDrag: 1,
        margin: 10,
        loop: true,
        center: true,
        autoplay: true,
        autoplayTimeout: 5600,
        autoplayHoverPause: 1,
        rtl: false,
        nav: true,
        navContainer: '#slider.jss-slider .jss-navigation .jss-navigation-buttons',
        dots: false,
        dotsContainer: '#slider.jss-slider .jss-navigation .jss-navigation-dots',
        autoHeight: true,
        lazyLoad: false,
        navText: [ ' ', ' ' ],
        smartSpeed: 300,
        onInitialize: jssInit_slider,
        onInitialized: jssInitEnd_slider
        , animateIn: 'fadeInRight', animateOut: 'fadeOutLeft'
    };
    slider = jQuery("#slider.jss-slider .owl-carousel").owlCarousel(options);

            var slider_anim = 0;
            slider.on("translate.owl.carousel", function(event) {
                slider_anim = 1;
            });
            slider.on("translated.owl.carousel", function(event) {
                if (slider_anim) {
                    jQuery("#slider .owl-item:not(:eq(" + event.item.index + ")) .jss-title > *").css("opacity", "0");

                    jssAnimText_slider(event.item.index);
                }
                slider_anim = 0;
            });

});

