$(".group870174").colorbox({rel:'group870174', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group870174").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery870174 = $("a[rel=group870174]").colorbox({maxWidth: "80%"});
$("a#openGallery870174").click(function(e){
    e.preventDefault();
    $gallery870174.eq(0).click();
});

