var sc_project=11965032; 
var sc_invisible=1; 
var sc_security="3c896546"; 
var sc_https=1;


$('#submit').on('click', function(e){
      var valid = this.form.checkValidity();
      $("#valid").html(valid);
      if (valid){
        e.preventDefault();
        $("#submit").attr("disabled", true);

        grecaptcha.execute('6LfoL6oUAAAAAKHEWF0g7zoaIzo118vUXhIDa2jL', {action: 'submit_form'}).then(function(token) {
            $('[name="g-recaptcha-response"]').val(token);
        }, function(reason) {
            console.log(reason);
        });

        $.ajax({
            type: "POST",
            url: "https://forms.superlander.com/",
            data: $("#contactform").serialize()
         }).done(function(data) {
            $("#success-form").show();
            $("#contactform").hide();
            $("#section-title").hide();
         }).fail(function(data){
            $("#submit").attr("disabled", false);
         });
      }
});
