div.script:
    var $timer = $("#lblTimeoutTimer");
        function update() {
            var myTime = $timer.html();
            var ss = myTime.split(":");
            var dt = new Date();
            dt.setHours(0);
            dt.setMinutes(ss[0]);
            dt.setSeconds(ss[1]);

            var dt2 = new Date(dt.valueOf() - 1000);
            var temp = dt2.toTimeString().split(" ");
            var ts = temp[0].split(":");

            $timer.html(ts[1] + ":" + ts[2]);

            if ((ts[1] === "02") && (ts[2] === "00")) {
                //modal alert
                bootbox.confirm({
                    title: "Your session is about to expire due to inactivity",
                    message: "Would you like to continue your session?",
                    buttons: {
                        confirm: {
                            label: 'Yes',
                            className: 'btn-success'
                        },
                        cancel: {
                            label: 'No',
                            className: 'btn-danger'
                        }
                    },
                    callback: function (result) {
                        console.log('This was logged in the callback: ' + result);
                        if (result) location.reload();
                        else $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
                    }
                });
            }
            if ((ts[1] === "00") && (ts[2] === "00")) {
                $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
            }
            else { setTimeout(update, 1000); }
        }

    $(document).konami({
        result: function () {
            var src = "sysimages/WireFrame.png";
            if (location.href.includes("/protected/")) src = "../sysimages/WireFrame.png";
            var img = new Image();
            img.src = src;
            img.setAttribute("style", "width: 161px; margin: 52px calc(50% - 161px / 2) 20px; display: block; max-width: 100%;");
            img.alt = "Logo";
            img.addEventListener("load", function () {
                $("header .HeaderLogo").html("");
                $(img).appendTo("header .HeaderLogo");
            });
        }
    });
    $(document).ready(function () {
        $(".SearchOpen").click(function () {
            $(".GoogleSearchContainer").addClass("Open");
        });
        $(".SearchClose").click(function () {
            $(".GoogleSearchContainer").removeClass("Open");
        });
        $(".LiquidButton").click(function () {
            if ($(".LiquidButton").attr("class").includes("LiquidDown")) {
                $(".LiquidHeader").addClass("Open");
                $(".LiquidHeader").addClass("navbar-fixed-top");
                var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) + 50;
                $('#TopUCs').css('margin-top', mt + 'px')
                $('.BodyLeftContainer').css('height', 'calc(100% - ' + mt + 'px)');
                $(".LiquidButton").addClass("LiquidUp");
                $(".LiquidButton").removeClass("LiquidDown");
            } else {
                $(".LiquidHeader").removeClass("Open");
                $(".LiquidHeader").removeClass("navbar-fixed-top");

                $(".LiquidButton").removeClass("LiquidUp");
                $(".LiquidButton").addClass("LiquidDown");
                var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) - 50;
                $('#TopUCs').css('margin-top', mt + 'px')
                $('.BodyLeftContainer').css('height', '');
            }
        });
        if ($(".LiquidHeader").hasClass("Open")) {
            $(".LiquidHeader").addClass("navbar-fixed-top");
            var mt = parseInt($('#TopUCs').css('margin-top').replace('px', '')) + 51;
            $('#TopUCs').css('margin-top', mt + 'px')
            $(".LiquidButton").removeClass("LiquidDown");
            $(".LiquidButton").addClass("LiquidUp");
        }
    });

    $("nav:first").accessibleMegaMenu({
        /* prefix for generated unique id attributes, which are required 
        to indicate aria-owns, aria-controls and aria-labelledby */
        uuidPrefix: "accessible-megamenu",

        /* css class used to define the megamenu styling */
        menuClass: "nav-menu",

        /* css class for a top-level navigation item in the megamenu */
        topNavItemClass: "nav-item",

        /* css class for a megamenu panel */
        panelClass: "sub-nav",

        /* css class for a group of items within a megamenu panel */
        panelGroupClass: "sub-nav-group",

        /* css class for the hover state */
        hoverClass: "hover",

        /* css class for the focus state */
        focusClass: "focus",

        /* css class for the open state */
        openClass: "open"
    });
    $.each($(".TopNav nav ul li ul"), function (index, value) {
        $(value).parent().click(function (e) {
            if (e.target == $(value).parent().find("> a")[0]) {
                e.preventDefault();
                var ul = $(this).find("ul")[0];
                if ($(ul).css("height") == "0px") {
                    setTimeout(function () {
                        $(ul).css("height", "auto");
                    }, 500);
                    $(ul).css("height", $(ul).attr("data-height") + "px");
                }
                else {
                    $(ul).css("height", $(ul).attr("data-height") + "px");
                    setTimeout(function () {
                        $(ul).css("height", "");
                    }, 1);
                }
            }
        });
        var _h = 0;
        $.each($(value).find("li"), function (i, v) {
            _h += $(v).outerHeight();
        });
        $(value).attr("data-height", _h.toString());
    })

