	$(function(){
		$(window).bind("resize", function(){ pgScreenSize(); calendarListView(); });

		//School dropdown
		$("#gb-school-dropdown > div").load("/cms/lib/NC01911140/Centricity/Template/16/school%20list/catawba-school-html.html", function(){
			var schoolDropdowns = $('.gb-school-list-section');
			var maxSchoolList = 0;
			var extraMenuItems = "";

			schoolDropdowns.each(function(k, v){
				if( $(this).height() > maxSchoolList ){
					maxSchoolList = $(this).outerHeight();
				}

				$('ul li', this).each(function(k1, v1){
					var h = k;
					h++;
					if( $('a', this).attr('href') == "/catawbaschools" ) h++; //Kick the district links up a level
					extraMenuItems +=  h + ", " + $(this).text() + ", " + $('a', this).attr('href') + ", _blank;"
				});
			});

			$('#gb-school-dropdown > div').css({"visibility": "visible", "display": "none"});

			schoolDropdowns.css("min-height", maxSchoolList);

			//Setup the extra menu items
			var extraMenuOptions = "Elementary Schools; Middle Schools; High Schools; District";


			var popularLinks = {
				"#gLink1": ["/Page/2", [5, "Calendar", "_blank"]],
				"#gLink2": ["/powerschool", [5, "PowerSchool", "_blank"]],
				"#gLink3": ["http://catawbaschools.instructure.com", [5, "Canvas", "_blank"]],
				"#gLink4": ["https://www.catawbaschools.net/mobile", [5, "Mobile App", "_blank"]],
				"#gLink5": ["/gafe", [5, "Google Apps", "_blank"]],
				"#gLink6": ["/news", [5, "News", "_blank"]]
			}
			temp = showHide(popularLinks, "", true);

			if( temp != "" ){
				extraMenuOptions += "; Popular Links";
				extraMenuItems += temp;
			}

			// RESPONSIVE MAIN MENU
			$("body").csRsMenu({
			        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "menuButtonParent"      : "#gb-custom-mystart",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "MENU",
			        "showSchools"           : "no",
			        "showTranslate"         : "yes",
			        "translateId"           : "none",
			        "showAccount"           : "yes",
			        "extraMenuOptions"      : extraMenuOptions, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			        "extraMenuItems"        : extraMenuItems, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			        "siteID"                : "4"
			});

			if( $('#rs-menu-btn span.icon-font').length == 0 ){
				$('#rs-menu-btn span').prepend('');
			}
		});

		var socialMediaIcons = {
			'#fb': ["https://www.facebook.com/pages/Catawba-County-Schools/106974738370"],
			'#tw': ["https://twitter.com/catawbaschools"],
			'#yt': ["https://www.youtube.com/channel/UCnq-r9_czDdz4NtM6hkVutQ"],
			'#gp': ["http://www.google.com"],
			'#ig': ["https://www.instagram.com/catawbaschools/"]
		};
		showHide(socialMediaIcons, "", false);

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "menuButtonParent"      : "#sp-content",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "PAGES",
		        "siteID"                : "4"
		});

		pgScreenSize();
		useChannelForSection();
		dynamicContent();
		customDropdowns('icon-triangle-up', 'icon-triangle-down');
		modEvents();	

        $('#gb-popular-links a').attr('tabindex', -1);
		$('#gb-popular-links-button button').click(function(e){
			var popularLinks = $('#gb-popular-links');
			var icon = $('#gb-popular-links-button span.icon-font');

			if( popularLinks.hasClass("open") ){
            	$(this).attr('aria-expanded', 'false');
				popularLinks
                	.removeClass("open")
                    .attr('aria-hidden', 'true')
                    .slideUp();
				icon.removeClass("icon-triangle-down").addClass("icon-triangle-up");
                $('a', popularLinks).attr('tabindex', -1);
			}else{
            	$(this).attr('aria-expanded', 'true');
				popularLinks
                	.addClass("open")
                    .attr('aria-hidden', 'false')
                    .slideDown();
				icon.removeClass("icon-triangle-up").addClass("icon-triangle-down");
                $('a', popularLinks).attr('tabindex', 0);
                $('a', popularLinks).first().focus();
			}
		});

        $('#gb-popular-links a').last().keydown(function(e){
        	if(e.keyCode == 9){
            	e.preventDefault();

                $('#gb-popular-links-button button').focus();
            }
        });

		$("#gb-channel-search").click(function(e){
			var searchButton = $('#gb-channel-search');
			if( searchButton.hasClass("open") ){
				searchButton.removeClass("open");
                searchButton.attr('aria-expanded', 'false');
				$('#gb-channel-search-input').attr('aria-hidden', 'true').slideUp();
                $('#gb-channel-search-input input').attr('tabindex', -1);
			}else{
				searchButton.addClass("open");
                searchButton.attr('aria-expanded', 'true');
				$('#gb-channel-search-input').attr('aria-hidden', 'false').slideDown();
                $('#gb-channel-search-input input').attr('tabindex', 0);
                setTimeout(function(e){
                	$('#gb-channel-search-input input').focus();
                }, 300);
            }
		});


		addFramesetTranslate();

		$(window).scroll(function(e){ 
			stickyHeader(1024);
		});

		if($('#hp-emerg-announcement .ui-widget-detail ul.ui-articles').children().length < 1){
			$('#hp-emerg-announcement-outer').hide();
		}

        $('.remove-').remove();
	});

	/*stickyHeader
		Use: It handles the sticky header that will make the header become fixed upon scrolling
		Parameter: shutoff -- this is a numeric value in which the width of the screen will stop the sticky header from activating.
	*/
	var scrollHookOn = 158;
	var scrollHookOff = 159;

	function stickyHeader(shutoff){
		var getWidth = $(window).width();

		if( getWidth >= shutoff ){
			var top = $(window).scrollTop();
				//check where the window is
			if(top >= scrollHookOn  && !$('#sticky-header').hasClass('on')){ //make it sticky
				$('#sticky-header-ghost').addClass('on').css('height', $('#sticky-header').outerHeight());
				$('#sticky-header').addClass('on');

			}else if( top <= scrollHookOff  && $('#sticky-header').hasClass('on')){ //unstick it
				$('#sticky-header').removeClass('on');
				$('#sticky-header-ghost').removeClass('on');
			}

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", $("#sticky-header").outerHeight(true));
                }
            }
		}else if( $('#sticky-header').hasClass('on') ){
			$('#sticky-header').removeClass('on');
			$('#sticky-header-ghost').removeClass('on');
		}
	}

	function pgScreenSize(){
		var getWidth = $(window).width();

		if (getWidth >= 1024) { // 960+
			multimediaRotator();
			sameHeights(getWidth);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			multimediaRotator();
			// Move hp right-bar right back into the right bar
			if( $('.hp.column.two .hp.right-bar.right').length != 1 ){
				$('.hp.column.two').append($('.hp.right-bar.right'));
			}
			if( $('#rs-menu-btn span.icon-font').length == 0 ){
				$('#rs-menu-btn span').prepend('');
			}
			sameHeights(getWidth);
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			multimediaRotator();
			// Move hp right-bar right below the columns
			if( $('.hp.column.two .hp.right-bar.right').length != 0 ){
				$('#hp-content').append($('.hp.right-bar.right'));
			}
			if( $('#rs-menu-btn span.icon-font').length == 0 ){
				$('#rs-menu-btn span').prepend('');
			}
			sameHeights("clear");
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			multimediaRotator();
			if( $('#rs-menu-btn span.icon-font').length == 0 ){
				$('#rs-menu-btn span').prepend('');
			}
			sameHeights("clear");
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			multimediaRotator();
			if( $('#rs-menu-btn span.icon-font').length == 0 ){
				$('#rs-menu-btn span').prepend('');
			}
			sameHeights("clear");
		}
	}

