	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };

	        //move some stuff around..
	        var block = $('.sw-calendar-block-date', this);

	        	//Get the month and day before we remove them
	        var month = $('.joel-month', this).html();
	        var day = $('.joel-day', this).html();
	        	//get rid of the month and day
	        block.children().remove();
	        	//grab the numeric date
	        var date = block.text();

	        var newBlock = '' + date + '\

\
	        			' + day + '\
	        			' + month + '\
	        		';
	        block.html(newBlock);
	    });
	}

