	function searchText(id) {

		$('#gb-channel-search-input input[type="text"]').keydown(function(e){
        	if(e.keyCode == 9){
            	$('#gb-channel-search').click();
            }
        });

		//DOC custom mystart search placeholder
        //SEARCH TEXT
        var inputText = "Search...";
        $(id).removeAttr("placeholder");


		$(id).attr("value", inputText);
		$(id).focus(function() {
			if($(this).val() == inputText) {
				$(this).val("");
			}
		});
		$(id).blur(function() {
			if($(this).val() == "") {
				$(this).val(inputText);
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = $(window).width();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

	/* showHide 
	** Use: To hide items based on a dynamic comparison, and if desired it will return you a formatted string to add into the mobile menu.
	** Params: 
		list -- this is an obj {} of items that you want to hide, or leave showing.
			*EXAMPLE*  { #id: [url *used for comparison*, [menuOptionNumber, label, target]] 
		comp -- is the value you want it to compare against to determine if it should be hidden or not.
		menuOption -- is a bool to tell the function whether or not you want a menu option string returned to you
	*/
	function showHide(list, comp, menuOption){
		var menuOptionString = "";

		$.each(list, function(k, v){
			if(v[0] === comp){
				$(k).hide();
			}else if(menuOption){
					//build out the menu option string
				menuOptionString += v[1][0] + "," + v[1][1] + "," + v[0] + "," + v[1][2] + ";";
			}
		});

		return menuOptionString;
	}

    /* customDropdowns
	**  Use: Sets up custom dropdown menus.
	**  Param: openIcon, closedIcon -- The classes that you want to mark the on/off state of the menu icons (dont pass the . or #)
	*/
    var openIcon = 'icon-triangle-up';
    var closedIcon = 'icon-triangle-down';
    var dropdownTimeout = ""; //Will hold our timeout object that will get passed among the dropdown functions.
	function customDropdowns(){

		$('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);

        $('.custom-dropdown button').on('keydown', function(e) { // Trigger the click event from the keyboard
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                e.preventDefault();

                $(this).click();
                if( !$(this).parent().hasClass('openMenu') ){
                    //clear our keyboard hooks since the menu is closing.
                    dropdownKeyboardFunctionality($('~ .dropdown', this), true);
                }else{
                    //add our keyboard hooks
                    dropdownKeyboardFunctionality($('~ .dropdown', this));
                }
            }else if(code == 39 || code == 40 && $(this).attr('aria-expanded') == 'true'){
                e.preventDefault();
                $('~ .dropdown a', this).first().focus();
            }else if( code == 9 ){
                if($(this).attr('aria-controls') != 'translate-option-list'){
                    clearDropdowns();
                }
            }
        });
		//Custom Dropdown Functionality
        $('.custom-dropdown > button').off('click').click(function(e){
            clearTimeout(dropdownTimeout);

            if( $(this).attr("aria-expanded") == "false" ){
                clearDropdowns()
                $('~ .dropdown', this).attr('aria-expanded', 'true').attr('aria-hidden', 'false').show();
                $(this).attr('aria-expanded', 'true');
                $(this).parent().addClass("openMenu");
                $('.dropdown-icon', this).removeClass(closedIcon).addClass(openIcon);
            }else{
                clearDropdowns();
            }
        });
        $('.custom-dropdown').hover(function(e){ //In
            clearTimeout(dropdownTimeout);
        },function(e){ //Out
            //canDropDownHide = true;
            dropdownTimeout = setTimeout(function(){
                clearDropdowns();
            }, 800);
        });
	}
    function dropdownKeyboardFunctionality(dropdown, clear){
        if( clear ){
            $('a', dropdown).off('keydown');
        }else{
            $('a', dropdown).first().trigger('focus');

            $('a', dropdown).keydown(function(e){
                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                var linkObj = $('a', dropdown);

                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();
                        e.stopImmediatePropagation();
                            // IS FIRST ITEM
                        if(linkObj.index($(this)) == 0) {
                            $(dropdown).siblings('button').focus();
                        } else {
                            linkObj[linkObj.index($(this)) - 1].focus();
                            //$(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                            // FOCUS FIRST ITEM
                            linkObj[0].focus();
                        } else {
                            // FOCUS NEXT ITEM
                            linkObj[linkObj.index($(this)) + 1].focus();
                        }
                    break;

                    case csKeyCodes.tab:
                    	clearDropdowns();
                    break;

                    default: //pass
                    break;
                }
            });
        }
    }
    function clearDropdowns(){
        $('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);
        $('.custom-dropdown.openMenu .dropdown-icon').removeClass(openIcon).addClass(closedIcon);
        $('.custom-dropdown.openMenu .dropdown').attr('aria-expanded', 'false').attr('aria-hidden', 'true').hide();
        $('.custom-dropdown.openMenu > button').removeClass('acc-open').attr('aria-expanded', 'false');
        $('.custom-dropdown.openMenu li').removeClass('active-list-item');
        dropdownKeyboardFunctionality($('.custom-dropdown.openMenu .dropdown'), true);
        $('.custom-dropdown.openMenu').removeClass("openMenu");
	}

	function setupBullets(){
		$('.ui-widget.app.navigation').each(function(k, v){ //** go through every app
			$(this).children('.ui-widget-detail')
			.children('ul')
			.children('li')
 			.each(function(k1, v1){ //** go through all of the apps links
				if($(this).find('.bullet').length > 1){
					$(this).find('.bullet').each(function(k2, v2){ //this has bullet children
						setBullet($(this));
					});
				}else{ //this is the only bullet level
					setBullet($(this).find('.bullet'));
				}
			});
		});
	}
	/*setBullet
		Use: to set bullets with chevrons appropriately 
		Parameter: bullet (jQuery Obj) - This is a jQuery Obj of the bullet
	*/
	function setBullet(bullet){
		if($(bullet).hasClass('expandable')){ //if it has children apply a different bullet
			$(bullet).addClass('icon-font icon-arrow-left');
		}else if( $(bullet).hasClass('collapsible') ){
			$(bullet).addClass('icon-font icon-arrow-down');
		}else{
			$(bullet).addClass('icon-font');
		}
	}

	/* sameHeights
		Use: To thoroughly confuse programmers, and make sure two columns are always touching the bottom of #hp-content
		Params: windowW - number/string -- will be used to check window size as passed in by pgScreenSize funct, and clear the sameHeights applied styles/listners. 
	*/
	function sameHeights(windowW){
		$('.hp.right-bar.right .ui-widget.app').last().css("min-height", 0);
		$('.hp.column.one .ui-widget.app').last().css("min-height", 0);

		if(windowW != "clear"){
				//Reset the heights so that we do not use them in future calcs
			$('.hp.right-bar.right .ui-widget.app').last().css("min-height", 0);
			$('.hp.column.one .ui-widget.app').last().css("min-height", 0);

				//Get the main container height
			var hpHeight = $('#hp-content').height();
			var galleryHeight = $('#hp-slideshow-outer').outerHeight();
			if(windowW < 1024){ //if we are below desktop view
				hpHeight = hpHeight - galleryHeight; //the gallery is moved into hp-content so lets account for it 
				galleryHeight = 0; //set the galleryHeight to zero so anywhere else it was being used will not have to deal with it
			}

				//Set the far left column
			var leftColLastAppHeight = $('.hp.column.one .ui-widget.app').last().css("margin", "0px").outerHeight();
			var leftColHeight = $('.hp.column.one').outerHeight();

				//Get the difference of the parent container and the left column then add it to the last apps height
		 	var newHeight = (hpHeight - leftColHeight) + leftColLastAppHeight;
			$('.hp.column.one .ui-widget.app').last().css({"min-height": newHeight});

				//Set the far right column 
			var rightColLastAppHeight = $('.hp.right-bar.right .ui-widget.app').last().css("margin", "0px").outerHeight();
			var rightColHeight = $('.hp.column.two').outerHeight();
			var combinedHeight = 0;
			if( windowW >= 1024){ //adjust how we account for the gallery height on the right column 
				combinedHeight = rightColHeight + galleryHeight;
			}else{
				combinedHeight = rightColHeight;
			}

		 	newHeight = (hpHeight - combinedHeight) + rightColLastAppHeight;
			$('.hp.right-bar.right .ui-widget.app').last().css({"min-height": newHeight});
			$('.hp.right-bar.left .ui-widget.app').last().css("margin", "0px");

			//$('#hp-content .ui-widget.app').resize(function(e){sameHeights(windowW);});
		}else{ 
			// will clear the set styles and listeners
			$('#hp-content').off('resize');
			$('.hp.right-bar.right .ui-widget.app').last().css("min-height", 0).addClass('last');
			$('.hp.column.one .ui-widget.app').last().css("min-height", 0).addClass('last');
		}
	}

