    function LoadRating427129() {
    var ModuleInstanceID = 4;
    var FlexDataID = 27129;

        GetContent("https://www.catawbaschools.net//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating427129", 2);
}

    function InsertRating427129() {
    var ModuleInstanceID = 4;
    var FlexDataID = 27129;
    var Rating = $('#swmoduleinstancerating-hid-rating427129').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating427129();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.catawbaschools.net//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating427129']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating427129Values();
                InsertRating427129($('#swmoduleinstancerating-hid-rating427129').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating427129Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating427129']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating427129').val((i + 1) / 2);
            }
        });
    }

