$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=%2f7m4b6%2fm1j7ecga9%2fhcGDg%3d%3d&SessionID=8dce38af-0346-4c72-9581-fb10e4514102&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f29%2f2020+9%3a47%3a54+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.64.245';
