    function rotateHeadlines() {
		$(".hp-rotating-headlines div.ui-widget.app.headlines").hide();
		$(".hp-rotating-headlines div.ui-widget.app.headlines").eq(0).show();
		/*$(".hp-rotating-headlines div.ui-widget.app.headlines").eq(0).each(function() {
			if(!$(".active", this).size()) {
				var index = 0;
				$("li", this).eq(index).addClass("active");
				$(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
			} else {
				var index = $(".active", this).index();
				$(".active", this).animate({ opacity: 0 }, 1500, function() { }); 
				$(".active", this).removeClass("active");
				if(index == $("li", this).length-1) {
					$("li", this).eq(0).addClass("active");
				}
				else {
					$("li", this).eq(index).next("li").addClass("active");
				}
				$(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
			}
		});*/

        if(!$(".hp-rotating-headlines .headlines .ui-articles li.active").length) {
            var index = 0;
            $(".hp-rotating-headlines .headlines .ui-articles li").eq(index).addClass("active").animate({ opacity: 1 });
        }

        $(".hp-rotating-headlines div.ui-widget.app.headlines").eq(0).each(function() {
			/*if(!$(".active", this).size()) {
				var index = 0;
				$("li", this).eq(index).addClass("active");
				$(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
			} else {*/
				var index = $(".active", this).index();
				$(".active", this).animate({ opacity: 0 }, 1500, function() { }); 
				$(".active", this).removeClass("active");
				if(index == $("li", this).length-1) {
					$("li", this).eq(0).addClass("active");
				}
				else {
					$("li", this).eq(index).next("li").addClass("active");
				}
				$(".active", this).animate({ opacity: 1 }, 1500, function() { setTimeout(function () { rotateHeadlines(); }, 8000); });
			//}
		});

        $(".hp-rotating-headlines .cs-headline-ctrl.next").click(function(e) {
            e.preventDefault();
            var index = $(".hp-rotating-headlines .headlines .ui-articles li.active").index();
            $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 0 });
            $(".hp-rotating-headlines .headlines .ui-articles li.active").removeClass("active");

            if(index == $(".hp-rotating-headlines .ui-articles li").length-1) {
                $(".hp-rotating-headlines .headlines .ui-articles li").eq(0).addClass("active");
            } else {
                $(".hp-rotating-headlines .headlines .ui-articles li").eq(index).next().addClass("active");
            }

            $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 1 });
        });

        $(".cs-headline-ctrl.next").keydown(function(e) {
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                var index = $(".hp-rotating-headlines .headlines .ui-articles li.active").index();
                $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 0 });
                $(".hp-rotating-headlines .headlines .ui-articles li.active").removeClass("active");

                if(index == $(".hp-rotating-headlines .ui-articles li").length-1) {
                    $(".hp-rotating-headlines .headlines .ui-articles li").eq(0).addClass("active");
                } else {
                    $(".hp-rotating-headlines .headlines .ui-articles li").eq(index).next().addClass("active");
                }

                $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 1 });
            }
        });

        $(".hp-rotating-headlines .cs-headline-ctrl.back").click(function(e) {
            e.preventDefault();
            var index = $(".hp-rotating-headlines .headlines .ui-articles li.active").index();
            $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 0 });
            $(".hp-rotating-headlines .headlines .ui-articles li.active").removeClass("active");
            if(index == 0) {
                $(".hp-rotating-headlines .headlines .ui-articles li:last-child").addClass("active");
            } else {
                $(".hp-rotating-headlines .headlines .ui-articles li").eq(index).prev().addClass("active");
            }

            $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 1 });
        });

        $(".cs-headline-ctrl.back").keydown(function(e) {
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                var index = $(".hp-rotating-headlines .headlines .ui-articles li.active").index();
                $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 0 });
                $(".hp-rotating-headlines .headlines .ui-articles li.active").removeClass("active");
                if(index == 0) {
                    $(".hp-rotating-headlines .headlines .ui-articles li:last-child").addClass("active");
                } else {
                    $(".hp-rotating-headlines .headlines .ui-articles li").eq(index).prev().addClass("active");
                }

                $(".hp-rotating-headlines .headlines .ui-articles li.active").animate({ opacity: 1 });
            }
        });
	}

