	function dynamicContent(){

    	//Added by Adam 8.28.17 - Case #02895499
        $(".extra-link").each(function(){
        	var linkText = $(this).text().trim();
            if(linkText == ""){
            	$(this).remove();
            }
        });

		// ********** MYSTART ********** //
        //DOC change district home button text
        $(".sw-mystart-button.home a span").text("SCSD Home");
        //DOC change my account button text
        $("#sw-myaccount").text("USER OPTIONS");
        //DOC change sign in button text
        $(".sw-mystart-button.signin a span").text("STAFF");
        //DOC change my passkeys button text
        $("#ui-btn-mypasskey span").text("PASSKEYS");

        //DOC if myview button exists, move into myaccount dropdown
        if($(".sw-mystart-button.pw").length) {
            var myviewBtn = $(".sw-mystart-button.pw").html();
            $("
" + myviewBtn + "").prependTo("#sw-myaccount-list");
            $(".sw-mystart-button.pw").remove();
        }
       /* if($(".sw-mystart-button.manage").length) {
            var manageBtn = $(".sw-mystart-button.manage").html();
            $("
" + manageBtn + "").prependTo("#sw-myaccount-list");
            $(".sw-mystart-button.manage").remove();
        }*/
        //MOVE SITE MANAGER INTO USER OPTIONS DROPDOWN - COMMENTED OUT ABOVE CODE AND ADDED THE FOLLOW CODE TO ADD SITE MANAGER - BY LYNDSAY - CASE 00562883 - 6.16.17
        if($(".sw-mystart-button.manage").length) {
            $('#sw-myaccount-list').append('
Site Manager');
            //MOVED THIS HERE SO THE DROPDOWN HTML GETS CHANGED AFTER THE SITE MANAGER BUTTON GETS ADDED - Adam - 10.3.17 - Case #02930614
            $(".sw-mystart-dropdown.user-options .sw-dropdown-list").empty().html($("#sw-myaccount-list").html());
        }
        $(".sw-mystart-button.manage").remove();


        if($(".sw-mystart-button.signin").length) {
        	$(".sw-mystart-button.signin").appendTo(".footer-options.left");
        }
        if($(".sw-mystart-button.register").length) {
        	$(".sw-mystart-button.register").appendTo(".footer-options.left");
        }
        if($("#sw-mystart-mypasskey").length) {
        	$("#sw-mystart-mypasskey").appendTo(".footer-options.left");
        }

        //DOC move district home, select a school, and search out of system mystart
        $(".sw-mystart-dropdown.schoollist").prependTo("#gb-mystart-inner");
        $(".sw-mystart-button.home").prependTo("#gb-mystart-inner");
        $("#sw-search-button span").addClass("springfieldCity-icon-font").html("");
        $("#sw-mystart-search").appendTo("#gb-mystart-inner");

        $("#sw-myaccount").focus(function() {
        	$(".sw-mystart-dropdown.translate .sw-dropdown").slideUp();
        });

		//UNBIND SYSTEM CLICK EVENT - MYSTART DROPDOWNS
		/*$(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown", function(e) {
