        if(linkOneWidth > linkTwoWidth && linkOneWidth > linkThreeWidth) {
        	$(".hp-extra-links .extra-link span").css("width",linkOneWidth + "px");
        }
        //if two is largest
        if(linkTwoWidth > linkOneWidth && linkTwoWidth > linkThreeWidth) {
        	$(".hp-extra-links .extra-link span").css("width",linkTwoWidth + "px");
        }
