	var announcement = false;
    var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 }; //added by Lyndsay - Case 00515641 - 4.13.17

	$(window).load(function(){
		searchText();

		if($(".hp").length){
			$('.app.headlines').masonry();
			/* Added by Jeremy - Case #00169913 */
			// Cause Homepage upcoming event li s to match height. 
			var maxLiHeight = 0;
			$('#hp-bottom-app .upcomingevents .ui-articles > li').css('min-height', 0);
			$('#hp-bottom-app .upcomingevents .ui-articles > li').each(function(k, v){
				if( $(v).outerHeight() > maxLiHeight ) maxLiHeight = $(v).outerHeight();
			});
			$('#hp-bottom-app .upcomingevents .ui-articles > li').css('min-height', maxLiHeight);
		}

		$('.ui-widget.app.navigation').each(function(k, v){ //** go through every app
			$(this).children('.ui-widget-detail')
			.children('ul')
			.children('li')
 			.each(function(k1, v1){ //** go through all of the apps links
				if($(this).find('.bullet').length > 1){
					$(this).find('.bullet').each(function(k2, v2){ //this has bullet children
						setBullet($(this));
					});
				}else{ //this is the only bullet level
					setBullet($(this).find('.bullet'));
				}
			});
		});
	});

	/*setBullet
