    function accessibility() { //added function by Lyndsay - Case 00515641 - 4.13.17
    	$('.custom-dropdown').on('keyup', function(e) { // Trigger the click event from the keyboard
                  var code = e.which;
                  if ((code === 13) || (code === 32)) {
                      //e.preventDefault();
                      $(this).addClass("openMenu");
                      $("ul > li > a", this).attr("tabindex", "0");
                      $("ul > li:first-of-type > a", this).focus();

              } 

              $('.custom-dropdown').on('keydown', function(e) { // Trigger the click event from the keyboard
                  var code = e.which;
                  // 13 = Tab
                  if (code === 9) {
                      if(e.shiftKey) {
                             //Focus previous input
							$(this).removeClass("openMenu");
                            $("ul > li > a", this).attr("tabindex", "-1");
                          }
                          else {
                             //Focus next input
                             //e.preventDefault();
                             $(this).removeClass("openMenu");
                             $("ul > li > a", this).attr("tabindex", "-1");

                          }
                  }
              }); 

              $('.custom-dropdown ul > li > a').on('keydown', function(e) { // Trigger the click event from the keyboard
              		if (e.keyCode == 38) {
                    	e.preventDefault();
						$(this).parent('li').prev('li').find('a').first().focus();
                    }
              });

              $('.custom-dropdown ul > li > a').on('keydown', function(e) { // Trigger the click event from the keyboard
              		if (e.keyCode == 40) {
                    	e.preventDefault();
						$(this).parent('li').next('li').find('a').first().focus();
                    }
              });
        }); 
    }

    function channelFixes() { // added function as part of Case 00515641 by Lyndsay - 4.13.17
            $('.sw-channel-item > a').keydown(function(e){ 
            var c = e.which, 
            href = $(this).attr('href'); 
            if(c == 13 || c == 32) { 
            	window.location.href = href; 
            } 
        }); 
        $(".sw-channel-item a").each(function (ind) { 
            var isTabPressed = false; 

            $(this).parents('.sw-channel-item').keydown(function (e) { 
                isTabPressed = e.keyCode == 9; 
            }); 

            $(this).parents('.sw-channel-item').keyup(function (e) { 
                isTabPressed = false; 
            }); 

            $(this).blur(function (event) { 
                if (isTabPressed && ($(this).parents('.sw-channel-item').next().length == 0)) { 
                    $(".sw-channel-dropdown").hide(); 
                    $(this).parents('.sw-channel-item').removeClass("hover"); 
                    var subList = $(this).parents('.sw-channel-item').children('ul'); 
                    if ($.trim(subList.html()) !== "") { 
                    	subList.attr("aria-hidden", "true"); 
                    } 
                } 
            }); 
        }); 
    }
	function setBullet(bullet){
		if($(bullet).hasClass('expandable')){ //if it has children apply a different bullet
			$(bullet).addClass('icon-font icon-arrow-down2');
		}else if( $(bullet).hasClass('collapsible') ){
			$(bullet).addClass('icon-font icon-arrow-right2');
		}
	}

